        function calculateTensions(m, x, y) {
            const L = 10;
            const g = 9.81;
            const theta1 = Math.atan(y / x);
            const theta2 = Math.atan(y / (L - x));
            const Fg = m * g;
            tensionT1 = Fg * Math.cos(theta2) / Math.sin(theta1 + theta2);
            tensionT2 = Fg * Math.cos(theta1) / Math.sin(theta1 + theta2);
            tensionT1 = parseFloat(tensionT1.toFixed(4));
            tensionT2 = parseFloat(tensionT2.toFixed(4));
        }

