    <script src="https://cdnjs.cloudflare.com/ajax/libs/numeric/1.2.6/numeric.min.js"></script>
    <script>
        const tensionSlider = document.getElementById('slider-tension');
        const x1Slider = document.getElementById('slider-x1');
        const x2Slider = document.getElementById('slider-x2');
        let x3Slider = document.getElementById('slider-x3');
        const m1Slider = document.getElementById('slider-m1');
        const m2Slider = document.getElementById('slider-m2');
        const m3Slider = document.getElementById('slider-m3');
        const playPauseButton = document.getElementById('btn-play-pause');
        const resetButton = document.getElementById('btn-reset');

        let isAnimating = false;
        let t = 0;
        let y = [0, 0, 0];
        let v = [0, 0, 0];
        let ω = [0, 0, 0];
        let A = [[0, 0, 0], [0, 0, 0], [0, 0, 0]];
        let c = [0, 0, 0];

        function resetSimulation() {
            isAnimating = false;
            playPauseButton.textContent = '▶';
            t = 0;
            y = [parseFloat(x1Slider.value), parseFloat(x2Slider.value), parseFloat(x3Slider.value)];
            const M = numeric.diag([parseFloat(m1Slider.value), parseFloat(m2Slider.value), parseFloat(m3Slider.value)]);
            const K = parseFloat(tensionSlider.value) * numeric.matrix([[2, -1, 0], [-1, 2, -1], [0, -1, 2]]);
            const C = numeric.inv(M).mult(K).mult(numeric.inv(M));
            const [λ, z] = numeric.eig(C);
            ω = λ.map(Math.sqrt);
            A = z.map(row => row.map(val => val / Math.sqrt(val * val + row[1] * row[1] + row[2] * row[2])));
            c = numeric.dot(numeric.multiply(A, numeric.diag(y)), [1, 1, 1]);
        }

        function updateAnimations() {
            y = [
                c[0] * Math.cos(ω[0] * t) + c[1] * Math.cos(ω[1] * t) + c[2] * Math.cos(ω[2] * t),
                c[0] * Math.sin(ω[0] * t) + c[1] * Math.sin(ω[1] * t) + c[2] * Math.sin(ω[2] * t),
                c[0] * Math.cos(ω[0] * t + Math.PI) + c[1] * Math.cos(ω[1] * t) + c[2] * Math.cos(ω[2] * t)
            ];
            v = [-ω[0] * c[0] * Math.sin(ω[0] * t), -ω[1] * c[1] * Math.sin(ω[1] * t), ω[2] * c[2] * Math.sin(ω[2] * t)];
            updatePlots();
        }

        function updatePlots() {
            // Update displacement plot
            const displacementData = [{x: Array.from({length: 500}, (_, i) => i / 100), y: y.map(val => numeric.dot(A, [val, 0, 0]).pop())}];
            Plotly.newPlot('displacement-plot-container', [displacementData.map(val => ({x: val.x, y: val.y, type: 'scatter', line: {color: 'blue'}}))]);

            // Update velocity plot
            const velocityData = [{x: Array.from({length: 500}, (_, i) => i / 100), y: v.map(val => numeric.dot(A, [0, 0, val]).pop())}];
            Plotly.newPlot('velocity-plot-container', [velocityData.map(val => ({x: val.x, y: val.y, type: 'scatter', line: {color: 'blue'}}))]);
        }

        function animate() {
            if (isAnimating) {
                t += 0.01;
                updateAnimations();
                requestAnimationFrame(animate);
            }
        }

        playPauseButton.addEventListener('click', () => {
            isAnimating = !isAnimating;
            if (isAnimating) {
                playPauseButton.textContent = '❚❚';
                animate();
            } else {
                playPauseButton.textContent = '▶';
            }
        });

        resetButton.addEventListener('click', resetSimulation);

        tensionSlider.addEventListener('input', resetSimulation);
        x1Slider.addEventListener('input', () => {
            resetSimulation();
            x3Slider.value = 0;
        });
        x2Slider.addEventListener('input', () => {
            resetSimulation();
            x1Slider.value = parseFloat(x2Slider.value);
        });
        x3Slider.addEventListener('input', resetSimulation);
        m1Slider.addEventListener('input', resetSimulation);
        m2Slider.addEventListener('input', resetSimulation);
        m3Slider.addEventListener('input', resetSimulation);

        resetSimulation();
    </script>
