const canvasWidth = 700;
const canvasHeight = 500;
const pointRadius = 6;
const strokeWeight = 1;
const borderWidth = 2;

let mode = 'move';
let points = [];
let draggedPoint = null;
let algorithmState = {
  step: 0,
  hullPointsIndices: [],
  fullHull: [],
  leftmostPointIndex: null,
  currentPointIndex: null,
  candidateLines: [],
  explanationSteps: []
};

function setup() {
  const canvas = createCanvas(canvasWidth, canvasHeight);
  canvas.parent('canvas-container');
  background(255);
  noFill();
  stroke(0);
  strokeWeight(borderWidth);
  rect(0, 0, width, height);

  // Initial points setup
  points = [
    createVector(150, 350),
    createVector(250, 150),
    createVector(350, 250),
    createVector(550, 180),
    createVector(480, 320),
    createVector(420, 400)
  ];

  drawPoints(points, color('lightGreen'), color('black'));
}

function draw() {
  if (mode === 'move' && draggedPoint !== null) {
    points[draggedPoint].x = mouseX;
    points[draggedPoint].y = mouseY;
    drawPoints(points, color('lightGreen'), color('black'));
  } else {
    if (points.length > 0) {
      drawPoints(points, mode === 'algorithm' ? color('darkBlue') : color('lightGreen'), color('black'));
    }
  }

  if (mode === 'algorithm' && algorithmState.step > 0) {
    switch(algorithmState.step) {
      case 1:
        // Step 1 drawing logic
        drawPoints(points, color('darkBlue'), color('black'));
        break;
      case 2:
        // Step 2 drawing logic
        drawCandidateLines();
        drawCurrentHullEdge();
        drawPoints(points, color('darkBlue'), color('black'));
        break;
      case 3:
        // Step 3 drawing logic
        if (algorithmState.candidateLines.length > 0) {
          drawCandidates(algorithmState.candidateLines);
        }
        drawCurrentHullEdge();
        drawPoints(points, color('darkBlue'), color('black'));
        break;
    }
  }
}

function mouseClicked() {
  if (mode === 'move') {
    for (let i = 0; i < points.length; i++) {
      if (dist(mouseX, mouseY, points[i].x, points[i].y) < pointRadius) {
        draggedPoint = i;
        return;
      }
    }
  }
}

function mouseReleased() {
  if (mode === 'move' && draggedPoint !== null) {
    draggedPoint = null;
    updateStepControls();
  }
}

function drawPoints(points, fillColor, strokeColor) {
  background(255);
  noFill();
  stroke(strokeColor);
  strokeWeight(borderWidth);
  rect(0, 0, width, height);

  fill(fillColor);
  stroke(strokeColor);
  for (let i = 0; i < points.length; i++) {
    ellipse(points[i].x, points[i].y, pointRadius * 2);
    line(points[i].x - 3, points[i].y, points[i].x + 3, points[i].y);
    line(points[i].x, points[i].y - 3, points[i].x, points[i].y + 3);
  }

  // Drawing candidate lines, hull edges
  if (mode === 'algorithm') {
    stroke(strokeColor);
    strokeWeight(2);
    for (let i = 0; i < algorithmState.candidateLines.length; i++) {
      const [fromIndex, toIndex] = algorithmState.candidateLines[i];
      line(points[fromIndex].x, points[fromIndex].y, points[toIndex].x, points[toIndex].y);
    }
  }
}

function drawCurrentHullEdge() {
  if (algorithmState.currentPointIndex !== null && algorithmState.hullPointsIndices.length > 1) {
    const prevPointIndex = algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length - 2];
