    Plotly.react('plot-bottom', [{
        x: stateData.time,
        y: stateData.E2,
        mode: 'lines',
        name: 'E₂(t)',
        line: { color: 'black' }
    }, {
        x: stateData.time,
        y: stateData.E1,
        mode: 'lines',
        name: 'E₁(t)',
        line: { color: 'magenta' }
    }, {
        x: stateData.time,
        y: stateData.E0,
        mode: 'lines',
        name: 'E₀(t)',
        line: { color: 'orange' }
