    <script>
        // Initialize MathJax to render the formula
        MathJax.typeset();

        // Define the color array
        const colorArray = ['#ffff80', '#98fb98', '#7fffd4', '#00ffff', '#87cefa', '#6495ed', '#9370db', '#8a2be2'];

        // Slider and input number synchronization
        const sliderN = document.getElementById('slider-n');
        const inputN = document.getElementById('input-n');

        sliderN.addEventListener('input', () => {
            inputN.value = sliderN.value;
            update();
        });

        inputN.addEventListener('input', () => {
            sliderN.value = inputN.value;
            update();
        });

        function update() {
            const n = parseInt(inputN.value);

            // Function plot canvas
            function sketchFunctionPlot(p) {
                p.setup = function() {
                    p.createCanvas(document.getElementById('canvas-function').width, document.getElementById('canvas-function').height);
                    p.colorMode(p.HSB, 255);
                    drawFunctionPlot();
                };

                p.redraw = function() {
                    p.background(220);
                    drawFunctionPlot();
                };

                function drawFunctionPlot() {
                    p.background(220);
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.fill(255, 0, 0);
                    p.line(0, p.height / 2, p.width, p.height / 2); // X-axis
                    p.line(p.width / 2, 0, p.width / 2, p.height); // Y-axis

                    // Axes ticks and labels
                    p.strokeWeight(0.5);
                    for (let x = 1; x <= 10; x++) {
                        p.line(x * p.width / 10, p.height / 2, x * p.width / 10, p.height / 2 + 5);
                        p.textAlign(p.CENTER);
                        p.text(x, x * p.width / 10, p.height / 2 + 15);
                    }
                    for (let y = 0.5; y <= 2.2; y += 0.5) {
                        p.line(p.width / 2, p.height * (1 - y / 2.2), p.width / 2 - 5, p.height * (1 - y / 2.2));
                        p.textAlign(p.CENTER);
                        p.text(y.toFixed(1), p.width / 2 - 15, p.height * (1 - y / 2.2));
                    }

                    // Function plot y = 2/x
                    p.stroke(0, 0, 255);
                    p.strokeWeight(2);
                    for (let x = 0.5; x <= p.width / 10 + 1; x += 0.01) {
                        let y = 2 / x;
                        if (y > 0) {
                            p.line(x * p.width / 10, p.height - p.height * y / 2.2, (x + 0.01) * p.width / 10, p.height - p.height * (2 / (x + 0.01)) / 2.2);
                        }
                    }

                    // Rectangles
                    for (let k = 1; k <= n; k++) {
                        let colorIndex = (k - 1) % colorArray.length;
                        p.stroke(colorArray[colorIndex]);
                        p.fill(colorArray[colorIndex]);
                        p.rect(k * p.width / 10, p.height - p.height * (2 / (k + 1)) / 2.2, p.width / 10, p.height * (2 / k - 2 / (k + 1)) / 2.2);

                        if (k <= 3) {
                            p.line(k * p.width / 10, p.height, k * p.width / 10, p.height - p.height * (2 / k) / 2.2);
                        }
                    }

                    // Formula label
                    const formulaLabel = document.getElementById('formula-label');
                    formulaLabel.innerHTML = '$$y = \\frac{2}{x}$$';
                    formulaLabel.style.color = colorArray[colorIndex];
                }
            }

            // Summation plot canvas
            function sketchSumPlot(p) {
                p.setup = function() {
                    p.createCanvas(document.getElementById('canvas-sum').width, document.getElementById('canvas-sum').height);
                    p.colorMode(p.HSB, 255);
                    drawSumPlot();
                };

                p.redraw = function() {
                    p.background(220);
                    drawSumPlot();
                };

                function drawSumPlot() {
                    p.background(220);
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.fill(255, 0, 0);
                    p.line(0, p.height, p.width, p.height); // X-axis
                    p.line(0, 0, 0, p.height * 2.2 / 2.2); // Y-axis

                    // Axes ticks and labels
                    p.strokeWeight(0.5);
                    for (let y = 1; y <= 2; y++) {
                        p.line(0, p.height - p.height * y / 2.2, p.width / 2, p.height - p.height * y / 2.2);
                        p.textAlign(p.LEFT);
                        p.text(y, 10, p.height - p.height * y / 2.2);
                    }

                    // Rectangles
                    let yStack = 0;
                    for (let k = n; k > 0; k--) {
                        let colorIndex = (k - 1) % colorArray.length;
                        let h = 2 / k - 2 / (k + 1);
                        p.stroke(colorArray[colorIndex]);
                        p.fill(colorArray[colorIndex]);
                        p.rect(0, p.height - p.height * (yStack + h) / 2.2, p.width / 2, p.height * h / 2.2);
                        yStack += h;
                    }
                }
            }

            // Initialize sketches
            new p5(sketchFunctionPlot, 'canvas-function');
            new p5(sketchSumPlot, 'canvas-sum');
        }

        // Initial update
        update();
    </script>
</body>
</html>
