                let isPlaying = false;
                let scale = 50;
                let m = 1;
                let M = 1;
                let g = 9.8;
                let l = 2;
                let theta = 0.5236;
                let theta_dot = 0;
                let x = 0;
                let x_dot = 0;
                let time = 0;
                const dt = 0.01;
                const canvasWidth = 500;
                const canvasHeight = 500;

                const canvas = document.getElementById('visualization-area');
                const context = canvas.getContext('2d');

                function setup() {
                    createCanvas(canvasWidth, canvasHeight);
                    noStroke();
                }

                function draw() {
                    background(255);
                    translate(width / 2, height / 2);
                    drawPendulum();
                    if (isPlaying) {
                        updatePhysics();
                    }
                    requestAnimationFrame(draw);
                }

                function drawPendulum() {
                    const px = x * scale;
                    const py = -l * scale * Math.cos(theta);
                    const bx = px + l * scale * Math.sin(theta);
                    const by = py - l * scale * Math.cos(theta);

                    fill(255, 0, 0);
                    rect(-10, py - 5, 20, 10); // Pivot

                    fill(255, 165, 0);
                    noStrokeDrawCircle(bx, by, 10); // Bob

                    stroke(0);
                    strokeWeight(2);
                    line(px, py, bx, by); // Connector
                }

                function noStrokeDrawCircle(x, y, radius) {
                    context.beginPath();
                    context.arc(x, y, radius, 0, TWO_PI);
                    context.fill();
