                function updatePhysics() {
                    const theta_ddot = (-m * l * Math.sin(theta) * Math.cos(theta) * theta_dot ** 2 - g * (M + m) * Math.sin(theta)) /
                                       (l * (M + m * Math.sin(theta) ** 2));
                    theta_dot += theta_ddot * dt;
                    theta += theta_dot * dt;
                    x_dot = 0 - (m * l * Math.cos(theta) * theta_dot) / (M + m);
                    x += x_dot * dt;
                    time += dt;
                }

                function stopAndUpdate(value) {
                    if (isPlaying) {
                        isPlaying = false;
                        document.getElementById('btn-play-pause').innerText = '▶';
                        setupPhysicsFromSliders();
                        drawPendulum();
