                    } else {
                        isPlaying = true;
                        document.getElementById('btn-play-pause').innerText = '||';
                    }
                }

                function resetSimulation() {
                    isPlaying = false;
                    document.getElementById('btn-play-pause').innerText = '▶';
                    stopAndUpdate();
                }

                function setupPhysicsFromSliders() {
                    theta = parseFloat(document.getElementById('slider-theta0').value);
