                    const value = theta * (PI / PI_MAX);
                    const closestPiValue = piValues.reduce((prev, curr) => Math.abs((value - prev) + (value - curr)) <= PI_MAX ? Math.min(Math.abs(prev), Math.abs(curr)) : prev);

                    if (closestPiValue < 0.1) {
                        document.getElementById('value-theta0').innerText = '0';
                    } else {
                        document.getElementById('value-theta0').innerText = `${Math.sign(closestPiValue) === Math.sign(value) ? Math.abs(closestPiValue).toFixed(2) : '-' + closestPiValue.toFixed(2)}π`;
                    }
                    document.getElementById('value-v0').innerText = theta_dot.toFixed(1);
                    document.getElementById('value-length').innerText = l.toFixed(1);

                    x_dot = theta_dot;
                    x = 0;
                    time = 0;
                }

                document.getElementById('slider-theta0').addEventListener('input', function() {
                    stopAndUpdate(this.value);
                });

                document.getElementById('slider-v0').addEventListener('input', function() {
