<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-db-size">database size</label>
                <input type="range" id="slider-db-size" min="8" max="100" step="1" value="10">
                <div class="value-display">+<span id="display-db-size">10</span></div>
            </div>
            <div class="control-group">
                <label for="slider-iterations">number of iterations</label>
                <input type="range" id="slider-iterations" min="0" max="20" step="1" value="2">
                <div class="value-display">+<span id="display-iterations">2</span></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="probability-display">99.9% success probability</div>
    </div>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let canvas;
        let dbSize = 10;
        let iterations = 2;
        let amplitudes = new Array(dbSize).fill(1/Math.sqrt(dbSize));
        let targetIndex = 6;

        function setup() {
            canvas = createCanvas(windowWidth * 0.8, 400);
            canvas.parent('canvas-container');
            dbSize = Number(document.getElementById('slider-db-size').value);
            iterations = Number(document.getElementById('slider-iterations').value);
            document.getElementById('display-db-size').innerText = dbSize;
            document.getElementById('display-iterations').innerText = iterations;
            updateAmplitudes();
            calculateProbability();
        }

        function draw() {
            background(255);
            rectMode(CENTER);
            fill(80);
            stroke(0);

            // Original amplitudes
            drawPlot(amplitudes, "#808080", 10, 0, N);

            // After first inversion
            let oracleAmplitudes = [...amplitudes];
            oracleAmplitudes[targetIndex] *= -1;
            drawPlot(oracleAmplitudes, "#808080", 10, 100, N);

            // After first diffusion
            let diffusionMean = mean(oracleAmplitudes);
            let diffusionAmplitudes = oracleAmplitudes.map(a => 2 * diffusionMean - a);
            drawPlot(diffusionAmplitudes, "#808080", 10, 200, N);

            // After k iterations
            amplitudes = [...diffusionAmplitudes];
            for (let i = 0; i < iterations; i++) {
                let mean = mean(amplitudes);
                amplitudes = amplitudes.map(a => 2 * mean - a);
            }
            drawPlot(amplitudes, "#FFA500", 10, 300, N);

            drawParetheses(0, N);
        }

        function drawPlot(amplitudes, color, yPos, start, end) {
            let barHeight = 40;
            let barWidth = 5;
            let spacing = (end - start) / amplitudes.length;
            for (let i = 0; i < amplitudes.length; i++) {
                let x = (start + i * spacing) * width / (end - start);
                let h = amplitudes[i] * barHeight;
                fill(color);
                noStroke();
                rect(x, height - yPos - h / 2, barWidth, h);
            }
        }

        function mean(arr) {
            return arr.reduce((a, b) => a + b) / arr.length;
        }

        function drawParetheses(start, end) {
            let x1 = (start * width) / (end - start);
            let x2 = ((end - 1) * width) / (end - start);
            let y = height - 100;
            let r = 50;

            noFill();
            stroke(0);
            strokeWeight(2);
            ellipse(x1, y, r * 2, r * 2);
            arc(x2, y, r * 2, r * 2, 180, 360);

            fill("#808080");
            stroke(0);
            strokeWeight(1);
            textSize(24);
            textAlign(CENTER, CENTER);
            text("Original Amplitudes", x1 + r, y + r);
        }

        function updateAmplitudes() {
            dbSize = Number(document.getElementById('slider-db-size').value);
            iterations = Number(document.getElementById('slider-iterations').value);
            document.getElementById('display-db-size').innerText = dbSize;
            document.getElementById('display-iterations').innerText = iterations;
            amplitudes = new Array(dbSize).fill(1/Math.sqrt(dbSize));
            targetIndex = 6;
        }

        function calculateProbability() {
            let k = iterations;
            let N = dbSize;
            let radians = Math.asin(1 / Math.sqrt(N));
            let angle_k = (2 * (k + 1)) * radians;
            let sin_squared = Math.pow(Math.sin(angle_k), 2);
            let probability = sin_squared * 100;
            document.getElementById('probability-display').innerText = `${probability.toFixed(1)}% success probability`;
        }

        function windowResized() {
            resizeCanvas(windowWidth * 0.8, 400);
        }

        // Event listeners for sliders
        document.getElementById('slider-db-size').addEventListener('input', function() {
            updateAmplitudes();
            redraw();
            calculateProbability();
        });

        document.getElementById('slider-iterations').addEventListener('input', function() {
            updateAmplitudes();
            redraw();
            calculateProbability();
        });
    </script>
</body>
