    function gammaFunction(z) {
        const P = 1 / (Math.sqrt(2 * Math.PI) * Math.sqrt(z + 5.5));
        const x = (z + 5.5) - (0.5 * (z + 5.5) - Math.floor(z + 0.5));
        return P * Math.exp(x * Math.log(z + 5.5) - (z + 0.5) * Math.log(x) - 5.5 * Math.log(1 + 1/x) + f(x));
    }

    function f(x) {
        const c = 1 / (x + 1/8);
        return c * (1 + 434.1/(x + 7/12) + 12.5/(x + 3/4) + 0.4/(x+ 1));
    }

    function gammaPdf(x, alpha, beta) {
        return Math.pow(x, alpha - 1) * Math.exp(-x / beta) / (Math.pow(beta, alpha) * gammaFunction(alpha));
    }

    function numericalIntegration(func, xValues) {
        let sum = 0;
        for (let i = 0; i < xValues.length - 1; i++) {
            sum += (func(xValues[i]) + func(xValues[i + 1])) / 2 * (xValues[i + 1] - xValues[i]);
        }
        return sum;
    }

