<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>Angle Bisectors on the Circumcircle</title>
    <style>
        body {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 100vh;
            margin: 0;
            background-color: #F5F5F5;
        }
        .canvas-container {
            position: relative;
            width: 500px;
            height: 500px;
        }
        canvas {
            border: 1px solid #6A5ACD;
        }
    </style>
</head>
<body>
    <h1>Angle Bisectors on the Circumcircle</h1>
    <p>Extend the angle bisectors of the triangle ABC to meet the circumcircle at A', B' and C'. Then AA' ⊥ B'C', BB' ⊥ A'C', and CC' ⊥ A'B'.</p>

    <div class="canvas-container" id="canvas-container">
        <canvas id="p5-canvas"></canvas>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let center = createVector(250, 250);
        let radius = 200;
        let points = [createVector(center.x, center.y)];
        let activePoint = null;

        function setup() {
            const canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');
        }

        function draw() {
            background(0xF0F0F0);
            drawCircumcircle();
            drawTriangle();
            drawExtendedBisectors();
            drawPointsAndLabels();
        }

        function drawCircumcircle() {
            strokeWeight(4);
            stroke(69, 93, 244); // SlateBlue
            noFill();
            ellipse(center.x, center.y, radius * 2, radius * 2);
        }

        function drawTriangle() {
            strokeWeight(4);
            stroke(69, 93, 244); // SlateBlue
            beginShape();
            vertex(points[1].x, points[1].y);
            vertex(points[2].x, points[2].y);
            vertex(points[3].x, points[3].y);
            endShape(CLOSE);
        }

        function drawExtendedBisectors() {
            strokeWeight(4);
            stroke(255, 165, 0); // Orange
            line(points[1].x, points[1].y, points[4].x, points[4].y);
            line(points[2].x, points[2].y, points[5].x, points[5].y);
            line(points[3].x, points[3].y, points[6].x, points[6].y);

            line(points[4].x, points[4].y, points[5].x, points[5].y);
            line(points[5].x, points[5].y, points[6].x, points[6].y);
            line(points[6].x, points[6].y, points[4].x, points[4].y);
        }

        function drawPointsAndLabels() {
            fillPoints(points.slice(0, 3), ['A', 'B', 'C']);
            fillPoints(points.slice(3, 6), ['A\'', 'B\'', 'C\'']);
            textLabels(['A', 'B', 'C'], points.slice(0, 3));
            textLabels(['A\'', 'B\'', 'C\''], points.slice(3, 6));
        }

        function fillPoints(pointsArray, labels) {
            for (let i = 0; i < pointsArray.length; i++) {
                let angle = atan2(pointsArray[i].y - center.y, pointsArray[i].x - center.x);
                let offset = i < 3 ? pi / 6 : pi / 4;
                let labelAngle = angle + offset;

                push();
                translate(pointsArray[i].x, pointsArray[i].y);
                rotate(labelAngle);
                stroke(0);
                strokeWidth(1);
                strokeWeight(1);
                fill(0xFFA500);
                ellipse(0, 0, 12, 12);
                line(-6, 0, 6, 0);
                line(0, -6, 0, 6);
                text(labels[i], -10, 10);
                pop();
            }
        }

        function angleToPoint(angle) {
            return p5.Vector.fromAngle(angle).mult(radius).add(center);
        }

        function pointToAngle(point) {
            return atan2(point.y - center.y, point.x - center.x);
        }

        function calculateNewPoint(point) {
            if(activePoint == point){
                let mousePos = createVector(mouseX, mouseY);
                let mouseAngleDiff = p5.Vector.sub(mousePos, center).heading();
                return angleToPoint(mouseAngleDiff);
            }else{
                return point;
            }
        }

        function calculateA(pointB, pointC){
            let averageAngle = atan2(sin(pointB.heading()) + sin(pointC.heading()), cos(pointB.heading()) + cos(pointC.heading()));
            let newPoint = createVector(center.x + radius * cos(averageAngle), center.y + radius * sin(averageAngle));
            newPoint.setMag(radius);
            return newPoint;
        }

        function calculateB(pointA, pointC){
            let averageAngle = atan2(sin(pointA.heading()) + sin(pointC.heading()), cos(pointA.heading()) + cos(pointC.heading()));
            let newPoint = createVector(center.x + radius * cos(averageAngle), center.y + radius * sin(averageAngle));
            newPoint.setMag(radius);
            return newPoint;
        }

        function calculateC(pointA, pointB){
            let averageAngle = atan2(sin(pointA.heading()) + sin(pointB.heading()), cos(pointA.heading()) + cos(pointB.heading()));
            let newPoint = createVector(center.x + radius * cos(averageAngle), center.y + radius * sin(averageAngle));
            newPoint.setMag(radius);
            return newPoint;
        }

        function mousePressed() {
            activePoint = detectPointByMouse(mouseX, mouseY);
        }

        function mouseDragged() {
            points[0] = calculateNewPoint(points[0]);
            activePoint = points[0];
            points[1] = calculateNewPoint(points[1]);
            activePoint = points[1];
            points[2] = calculateNewPoint(points[2]);
            activePoint = points[2];
        }

        function mouseReleased(){
            activePoint=null;
        }

        function detectPointByMouse(mouseX, mouseY){
            for(let i = 0; i < points.length; i++){
                if(p5.Vector.dist(createVector(mouseX, mouseY), points[i]) < 15){
                    return points[(i+1)%3];
                };
            }
            return null;
        }

        function textLabels(labels, pointsArray) {
            for (let i = 0; i < labels.length; i++) {
                push();
                translate(pointsArray[i].x + 15 * cos(i * 2 * PI / 3 + PI / 6), pointsArray[i].y + 15 * sin(i * 2 * PI / 3 + PI / 6));
                rotate(i * 2 * PI / 3);
                textSize(16);
                textAlign(CENTER, CENTER);
                fill(0);
                text(labels[i], 0, 0);
                pop();
            }
        }

        function setupPoints() {
            // Initial points setup
            let initialAngles = [7 * PI / 6, 11 * PI / 6, PI / 2];
            for (let i = 0; i < 3; i++) {
                let angle = initialAngles[i];
                let newPoint = createVector(center.x + radius * cos(angle), center.y + radius * sin(angle));
                newPoint.setMag(radius);
                points.push(newPoint);
            }

            points[3] = calculateA(points[1], points[2]);
            points[4] = calculateB(points[0], points[2]);
            points[5] = calculateC(points[0], points[1]);
        }

        setupPoints();
    </script>
</body>
</html>
