            border: none;
            border-radius: 5px;
            cursor: pointer;
            background-color: var(--inactive-bg, rgb(199,198,198));
            color: white;
            transition: background-color 100ms;
        }
        button:active { background-color: var(--active-bg, rgb(120,120,120)); }
        #btn-manhattan { --active-bg: rgb(117,117,117); }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <span>distance function</span>
            <button id="btn-manhattan">Manhattan</button>
            <button id="btn-euclidean">squared Euclidean</button>
            <button id="btn-chebyshev">Chebyshev</button>
        </div>
        <div id="grid-container"></div>
    </div>

    <script>
        const gridState = Array.from({ length: 9 }, () => Array.from({ length: 7 }, () => false));
        const currentDistanceFunction = 'manhattan';

        gridState[2][1] = true;
        gridState[6][4] = true;

        const btnManhattan = document.getElementById('btn-manhattan');
        btnManhattan.classList.add('--btn-active');

        const GRID_SIZE = 40;
        const gridDivs = [];

        function renderGrid() {
            const gridContainer = document.getElementById('grid-container');
            gridContainer.innerHTML = '';
            gridDivs.clear();

            for (let rowIdx = 0; rowIdx < gridState.length; rowIdx++) {
                const container = document.createElement('div');
                container.className = 'grid-row';
                for (var colIdx = 0; colIdx < gridState[0].length; colIdx++) {
                    const div = document.createElement('div');
                    const span = document.createElement('span');
                    const row = rowIdx + 1;
                    const col = colIdx + 1;
                    const totalDistances = Array(9).fill().map(() => Array(7).fill('∞'));
                    span.textContent = totalDistances[row][col];
                    gridDivs.push(div);

                    async function updateGrid() {
                        try {
                            const rowIdxStart = Math.max(row - 3, 0);
                            const rowIdxStop = Math.min(row + 3, gridState.length - 1);
                            for (let nextRowIdx2 = rowIdxStart; nextRowIdx2 <= rowIdxStop; nextRowIdx2++) {
                                const colIdxStart = Math.max(col - 3, 0);
                                const colIdxStop = Math.min(col + 3, gridState[0].length - 1);
                                for (let colIdx2 = colIdxStart; colIdx2 <= colIdxStop; colIdx2++) {
                                    if (gridState[rowIdx2][colIdx2]) {
                                        let sourceDistance;
                                        if (currentDistanceFunction === 'manhattan') {
                                            sourceDistance = Math.abs(rowIdx - rowIdx2) + Math.abs(col - colIdx2);
                                        } else if (currentDistanceFunction === 'euclidean') {
                                            sourceDistance = (rowIdx - rowIdx2) ** 2 + (col - colIdx2) ** 2;
                                        } else if (currentDistanceFunction === 'chebyshev') {
                                            sourceDistance = Math.max(Math.abs(rowIdx - rowIdx2), Math.abs(col - colIdx2));
                                        }

                                        totalDistances[nextRowIdx2][colIdx2] = Math.min(totalDistances[nextRowIdx2][colIdx2], sourceDistance);
                                    }
                                }
                            }

                            span.textContent = totalDistances[row][col];

                            if (totalDistances[row][col] === '∞') {
                                div.className = '--cell-off --cell-tooltip';
                                container.appendChild(div);
                                div.appendChild(span);
                                container.append(document.createElement('p')); // just invisible
                            } else if (totalDistances[row][col] === 0) {
                                div.className = '--cell-on';
                                container.appendChild(div);
                                div.appendChild(span);
                            } else {
                                div.className = '--cell-off';
                                container.appendChild(div);
                                div.appendChild(span);
                            }

                            await new Promise(resolve => setTimeout(resolve, 100));
                        } catch (error) {

                        }
                    }

                    div.style = `
                        position: relative;
                        background-color: white;
                        border: 1px solid black;
                        height: ${40}px;
                        width: ${40}px;
                        cursor: pointer;
                        text-align: center;
                        line-height: 40px;
                        font-size: 14px;
                        color: black;
                    `;

                    div.onclick = () => updateGrid();

                    container = container.appendChild(div);
                }
                gridContainer.appendChild(container);
            }
        }

        btnManhattan.addEventListener('click', () => {
            currentDistanceFunction = 'manhattan';
            btnManhattan.classList.add('--btn-active');
            btnEuclidean.classList.remove('--btn-active');
            btnChebyshev.classList.remove('--btn-active');
            renderGrid();
        });

        const btnEuclidean = document.getElementById('btn-euclidean');
        btnEuclidean.addEventListener('click', () => {
            currentDistanceFunction = 'euclidean';
            btnManhattan.classList.remove('--btn-active');
            btnEuclidean.classList.add('--btn-active');
            btnChebyshev.classList.remove('--btn-active');
            renderGrid();
        });

        const btnChebyshev = document.getElementById('btn-chebyshev');
        btnChebyshev.addEventListener('click', () => {
            currentDistanceFunction = 'chebyshev';
            btnManhattan.classList.remove('--btn-active');
            btnEuclidean.classList.remove('--btn-active');
            btnChebyshev.classList.addClass('--btn-active');
            renderGrid();
        });

        renderGrid();
    </script>
</body>
</html>
