document.addEventListener('DOMContentLoaded', function() {
    const selectMethod = document.getElementById('select-method');
    const sliderNum1 = document.getElementById('slider-num1');
    const sliderNum2 = document.getElementById('slider-num2');
    const btnIntYes = document.getElementById('btn-int-yes');
    const btnIntNo = document.getElementById('btn-int-no');
    const outputDisplay = document.getElementById('output-display');

    let num1 = 1.1;
    let num2 = 2;
    let isInteger = true;

    function updateDisplay() {
        const calculationMethod = selectMethod.value;
        const num1AsInt = parseInt(num1.toString().replace('.', ''));
        const num2AsInt = parseInt(num2.toString().replace('.', ''));
        const num1Factor = 10 ** (num1 - parseInt(num1));
        const num2Factor = 10 ** (num2 - parseInt(num2));
        const productOfInts = num1AsInt * num2AsInt;
        const productOfFactors = num1Factor * num2Factor;

        outputDisplay.innerHTML = '';

        if (calculationMethod === 'estimation') {
            outputDisplay.innerHTML += `
                <p>start with: ${num1} × ${num2}</p>
                <p>convert to: ${num1AsInt} × ${num2AsInt} = ${productOfInts}</p>
                <p>estimate as: ${Math.round(num1)} × ${Math.round(num2)} = ${Math.round(num1 * num2)}</p>
                <p>result is: ${num1 * num2}</p>
            `;
        } else {
            outputDisplay.innerHTML += `
                <p>start with: ${num1} × ${num2}</p>
                <p>rewrite as: (${num1AsInt} ÷ ${isInteger ? 1 : num1Factor}) × (${num2AsInt} ÷ ${isInteger ? 1 : num2Factor})</p>
                <p>= (${num1AsInt} × ${num2AsInt}) ÷ (${num1Factor} × ${num2Factor})</p>
                <p>= ${productOfInts} ÷ ${productOfFactors}</p>
                <p>= ${num1 * num2}</p>
            `;
        }
    }

    selectMethod.addEventListener('change', updateDisplay);

    sliderNum1.addEventListener('input', function() {
        num1 = parseFloat(this.value);
        document.querySelector('#slider-num1 + .slider-value').textContent = num1.toFixed(1);
        updateDisplay();
    });

    sliderNum2.addEventListener('input', function() {
        if (isInteger) {
            this.value = Math.round(parseFloat(this.value));
        }
        num2 = parseFloat(this.value);
        document.querySelector('#slider-num2 + .slider-value').textContent = num2.toFixed(2);
        updateDisplay();
    });

    btnIntYes.addEventListener('click', function() {
        isInteger = true;
        sliderNum2.setAttribute('step', '1');
        sliderNum2.value = Math.round(parseFloat(sliderNum2.value));
        this.classList.add('active');
        btnIntNo.classList.remove('active');
        updateDisplay();
    });

    btnIntNo.addEventListener('click', function() {
        isInteger = false;
        sliderNum2.setAttribute('step', '0.1');
        this.classList.add('active');
        btnIntYes.classList.remove('active');
        updateDisplay();
    });

    updateDisplay();
});
</script>
</body>
</html>
