    <script>
        const canvas = document.getElementById('canvas-material-bar');
        const ctx = canvas.getContext('2d');
        const circleWidth = 16; // mm
        const canvasWidth = canvas.width;
        const canvasHeight = canvas.height;

        function drawMaterialBar(t) {
            const s = 0.155 * Math.sqrt(t);
            const iceWidth = circleWidth - s - s;
            const linearGradient = ctx.createLinearGradient(0, 0, circleWidth * 16, 0);
            linearGradient.addColorStop(0, '#ff0000');
            linearGradient.addColorStop(1, '#ffffff');
            ctx.fillStyle = linearGradient;
            ctx.fillRect(0, 0, s * 16, canvasHeight);

            ctx.fillStyle = '#00BFFF';
            ctx.fillRect(s * 16, 0, s * 16, canvasHeight);

            ctx.fillStyle = '#003366';
            ctx.fillRect((circleWidth * 16 - iceWidth) / 2, 0, iceWidth / 2, canvasHeight);

            ctx.beginPath();
            ctx.moveTo(0, canvasHeight);
            ctx.lineTo(circleWidth * 16, canvasHeight);
            ctx.stroke();

            ctx.font = '12px Arial';
            ctx.fillText('0', 0, canvasHeight + 10);
            ctx.fillText('5', (6 * 16) / 16, canvasHeight + 10);
            ctx.fillText('10', (12 * 16) / 16, canvasHeight + 10);
            ctx.fillText('15', (16 * 16) / 16, canvasHeight + 10);
        }

        const slider = document.getElementById('slider-time');
        const span = document.getElementById('span-time-value');
        const resetButton = document.getElementById('btn-reset');

        function updateCharts(t) {
            const s = 0.155 * Math.sqrt(t);
            const lineTrace = {
                x: Array.from({ length: 101 }, (_, i) => i),
                y: Array.from({ length: 101 }, (_, i) => 0.155 * Math.sqrt(i)),
                mode: 'lines',
                type: 'line',
                line: { color: '#FFA500' }
            };

            const indicatorLine = {
                type: 'line',
                x0: t,
                y0: 0,
                x1: t,
                y1: s,
                line: { color: '#000000' }
            };

            Plotly.newPlot('div-plot', [lineTrace], {
                title: 'Stefan distance versus time',
                xaxis: { title: 'time (s)', range: [0, 100] },
                yaxis: { title: 'distance (mm)', range: [0, 1.6] },
                shapes: [indicatorLine]
            });

            span.textContent = t.toFixed(1);
            drawMaterialBar(t);
        }

        window.addEventListener('load', () => {
            updateCharts(9.8);
        });

        slider.addEventListener('input', () => {
            let t = parseFloat(slider.value);
            updateCharts(t);
        });

        resetButton.addEventListener('click', () => {
            slider.value = 0;
            updateCharts(0);
        });
    </script>
