<script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
<script>
const COLORS = {
  DarkRainbow: ['#1E4363', '#2E6341'],
  SiennaTones: ['#A0522D', '#CD853F', '#D2B48C'],
  RustTones: ['#B7410E', '#000033', '#FFFFFF'],
  FuchsiaTones: ['#C71585', '#000000', '#FFFFFF'],
  Grays: ['#404040', '#808080', '#C0C0C0']
};

let palette;
let zoom = false;
let steps = 2;

function setup() {
  const canvas = createCanvas(600, 600);
  canvas.parent('canvas-container');
  palette = COLORS['DarkRainbow'];

  noLoop();

  select('#input-steps').changed(() => {
    steps = select('#input-steps').value();
    updatePalette();
    redraw();
  });

  select('#select-color').changed(() => {
    const colorScheme = select('#select-color').value();
    palette = COLORS[colorScheme];
    updatePalette();
    redraw();
  });

  select('#checkbox-zoom').changed(() => {
    zoom = select('#checkbox-zoom').checked();
    updatePalette();
    redraw();
  });
}

function draw() {
  clear();

  translate(width / 2, height / 2);

  if (zoom) {
    scale(Math.pow(Math.SQRT1_2, steps));
    drawTiling(steps, 0, 0, 1, 0);
  } else {
    let maxSize;
    if (steps === 0) {
      maxSize = 100;
    } else {
      maxSize = Math.pow(7, steps) * 10;
    }
    scale(maxSize / 600);
    drawTiling(steps, 0, 0, 1, 0);
  }
}

function drawTiling(level, center_x, center_y, size, angle_rad) {
  if (level === 0) {
    // Draw a rhombus
    const angle_deg = angle_rad * 180 / PI;
    strokeWeight(1);
    noFill();
    stroke(palette[angle_deg % 3]);
    beginShape();
    vertex(center_x + size * cos(angle_rad), center_y + size * sin(angle_rad));
    vertex(center_x + size * cos(angle_rad + 2 * PI / 3), center_y + size * sin(angle_rad + 2 * PI / 3));
    vertex(center_x + size * cos(angle_rad + 4 * PI / 3), center_y + size * sin(angle_rad + 4 * PI / 3));
    vertex(center_x + size * cos(angle_rad + 6 * PI / 3), center_y + size * sin(angle_rad + 6 * PI / 3));
    endShape(CLOSE);

    // Draw optional diagonal line
    stroke(255, 255, 255, 128);
    line(center_x + size * cos(angle_rad), center_y + size * sin(angle_rad),
         center_x + size * cos(angle_rad + 2 * PI / 3), center_y + size * sin(angle_rad + 2 * PI / 3));
  } else {
    const scale_factor = 1 / Math.SQRT1_2;
    const angle_subdivision = atan(sqrt(3) / 5);

    for (let i = 0; i < 7; i++) {
      drawTiling(level - 1, center_x, center_y, size * scale_factor, angle_rad - angle_subdivision / 2 + i * angle_subdivision);
    }
  }
}

function updatePalette() {
  if (palette[0] === '#FFFFFF') {
    fill(palette[0]);
    rect(-50, -50, 100, 100);
  }
}
</script>
