    <div id="main-container">
        <div id="control-panel">
            <label for="slider-xD">distillate composition</label>
            <input type="range" id="slider-xD" min="0.80" max="1.00" step="0.01" value="0.84">
            <span id="label-xD">0.84</span>
        </div>
        <div id="plot">
            <script>
                const a = 9, b = -0.6;

                function y(x) { return (a * x) / (1 + (a - 1) * x + b * x * (1 - x)); }

                function r(x, xD) { return (xD - y(x)) / (y(x) - x); }

                function updatePlot(xD) {
                    let x_vals = [], r_vals = [];
                    for (let x = 0.001; x <= xD; x += 0.001) {
                        const rVal = r(x, xD);
                        if (!Number.isNaN(rVal) && 0 <= rVal && rVal < 5) {
                            x_vals.push(x);
                            r_vals.push(rVal);
                        }
                    }

                    let pinch_x = [], pinch_y = [];
                    if (r_vals.length > 2) {
                        for (let i = 1; i < r_vals.length - 1; i++) {
                            if (r_vals[i - 1] < r_vals[i] && r_vals[i] > r_vals[i + 1]) {
                                pinch_x.push(x_vals[i]);
                                pinch_y.push(r_vals[i]);
                                break;
                            }
                        }
                    }

                    const trace1 = {
                        x: x_vals,
                        y: r_vals,
                        mode: 'lines',
                        type: 'scatter',
                        line: { color: '#8B0000', width: 2.5 }
                    };

                    const trace2 = {
                        x: pinch_x,
                        y: pinch_y,
                        mode: 'markers',
                        type: 'scatter',
                        marker: { color: 'royalblue', size: 10 }
                    };

                    const layout = {
                        xaxis: { range: [0, 1.0], gridcolor: '#e0e0e0', dtick: 0.2, title: '' },
                        yaxis: { range: [0, 3.0], gridcolor: '#e0e0e0', dtick: 0.5, title: '' },
                        showlegend: false,
                        margin: { l: 40, r: 20, b: 40, t: 20 },
                        plot_bgcolor: 'white',
                        paper_bgcolor: 'white'
                    };

                    Plotly.react('plot', [trace1, trace2.length ? trace2 : []], layout);
                }

                document.getElementById('slider-xD').addEventListener('input', () => {
                    const xD = parseFloat(document.getElementById('slider-xD').value);
                    document.getElementById('label-xD').textContent = xD.toFixed(2);
                    updatePlot(xD);
                });

                updatePlot(0.84);
            </script>
        </div>
    </div>
</body>
</html>
