        let originY = canvasHeight / 2;
        let radius = 30;

        function setup() {
            myCanvas = createCanvas(canvasWidth, canvasHeight);
            background('#f0f0f0');
            noFill();
            stroke('#4a80b5');
            strokeWeight(2);

            myCanvas.parent('canvas-container');
