            p = 2;
            let angle = 0;

            if (ε === 0) {
                // Circle
                push();
                translate(originX - 2 * radius, originY);
                rotate(-PI / 2);
                arc(0, 0, 4 * radius, 4 * radius, 0, TWO_PI);
                pop();
            } else if (ε > 0 && ε < 1) {
                // Ellipse
                push();
                translate(originX - p / (1 + ε), originY);
                scale(1 + ε, 1);
                ellipse(0, 0, 2 * radius, 2 * radius);
                pop();
            } else if (ε === 1) {
                // Parabola
                push();
                translate(originX, originY);
                rotate(-PI / 2);
                for (let θ = -2.2; θ <= 2.2; θ += 0.1) {
                    let r = p / (1 + ε * cos(θ));
                    let x = r * cos(θ);
                    let y = r * sin(θ);
                    point(x * radius, -y * radius);
                }
                pop();
            } else if (ε > 1) {
                // Hyperbola
                let θ_min = -acos(-1 / ε);
                let θ_max = acos(-1 / ε);
                push();
                translate(originX, originY);
                rotate(-PI / 2);
                for (let θ = θ_min; θ <= θ_max; θ += 0.1) {
                    let r = p / (1 + ε * cos(θ));
                    if (r > 0) {
                        let x = r * cos(θ);
                        let y = r * sin(θ);
                        point(x * radius, -y * radius);
                    }
                }
                pop();
            }
        }

        function calculateAndDisplayEnergy() {
            let E = -0.25 * (1 - ε * ε);
            let energyValue = document.getElementById('energy-value');
