                energyValue.innerHTML = '\\(-\\frac{1}{4}\\)';
                MathJax.typeset();
            } else {
                energyValue.innerHTML = E.toFixed(2);
            }
        }

        function updateEccentricity(value) {
            ε = parseFloat(value);
            document.getElementById('value-eccentricity').textContent = ε.toFixed(1);
            draw();
        }
    </script>
