    <div id="app-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-points">collocation points</label>
                <input type="range" id="slider-points" min="5" max="30" step="1" value="20">
                <span id="label-points">20</span>
            </div>
            <div class="control-row">
                <label for="slider-time">t</label>
                <input type="range" id="slider-time" min="0.001" max="0.2" step="0.001" value="0.05">
                <span id="label-time">0.050</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        const sliderPoints = document.getElementById('slider-points');
        const sliderTime = document.getElementById('slider-time');
        const labelPoints = document.getElementById('label-points');
        const labelTime = document.getElementById('label-time');
        const plotContainer = document.getElementById('plot-container');

        const xValues = [];
        const yValues = [];
        const analyticalCurve = [];

        for (let i = 0; i <= 200; i++) {
            const x = i / 200;
            xValues.push(x);
            const t = parseFloat(sliderTime.value);
            analyticalCurve.push((8 / Math.PI**2) * Array.from({ length: 100 }, (_, n) => 
                (1 / n**2) * Math.sin(n * Math.PI / 2) * Math.sin(n * Math.PI * x) * Math.exp(-2 * n**2 * Math.PI**2 * t)
            ).reduce((acc, val) => acc + val, 0));
        }

        function updatePlot() {
            const numPoints = parseInt(sliderPoints.value);
            const chebyshevPoints = [];
            const chebyshevValues = [];

            for (let j = 0; j < numPoints; j++) {
                const x_j = (Math.cos(j * Math.PI / (numPoints - 1)) + 1) / 2;
                chebyshevPoints.push(x_j);
                const t = parseFloat(sliderTime.value);
                const T_j = (8 / Math.PI**2) * Array.from({ length: 100 }, (_, n) => 
                    (1 / n**2) * Math.sin(n * Math.PI / 2) * Math.sin(n * Math.PI * x_j) * Math.exp(-2 * n**2 * Math.PI**2 * t)
                ).reduce((acc, val) => acc + val, 0);
                chebyshevValues.push(T_j);
            }

            const data = [
                {
                    x: chebyshevPoints,
                    y: chebyshevValues,
                    mode: 'markers',
                    marker: { color: 'red', size: 8 }
                },
                {
                    x: xValues,
                    y: analyticalCurve,
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 3 }
                }
            ];

            const layout = {
                xaxis: { title: 'x', range: [0, 1], tickmode: 'linear', dtick: 0.2 },
                yaxis: { title: 'T', range: [-0.1, 1.1], tickmode: 'linear', dtick: 0.2 },
                showlegend: false,
                grid: { x: { color: '#f0f0f0' }, y: { color: '#f0f0f0' } },
                margin: { t: 20, r: 20, b: 40, l: 40 }
            };

            if (plotContainer.data && plotContainer.layout) {
                Plotly.react(plotContainer, data, layout);
            } else {
                Plotly.newPlot(plotContainer, data, layout);
            }
        }

        sliderPoints.addEventListener('input', () => {
            labelPoints.textContent = sliderPoints.value;
            updatePlot();
        });

        sliderTime.addEventListener('input', () => {
            labelTime.textContent = sliderTime.value.toFixed(3);
            updatePlot();
        });

        document.addEventListener('DOMContentLoaded', updatePlot);
    </script>
</body>
</html>
