        if (shape === 0) return Math.exp(-(v - mu) / sigma);
        const t = 1 + shape * ((v - mu) / sigma);
        return (1 / sigma) * Math.pow(t, shape + 1) * Math.exp(-t);
    }
    function calcReturnLevel(p, mu, sigma, xi) {
        if (xi === 0) return mu - sigma * Math.log(-Math.log(1 - 1 / p));
        const rlv = Math.exp(-(-Math.log(1 - 1 / p)) ** (-xi));
        return mu - (sigma / xi) * rlv;
    }
