                const material = new THREE.MeshPhongMaterial({ color: 0xffa500, shininess: 30 });
                const cone = new THREE.Mesh(geometry, material);
                const direction = new THREE.Vector3(vertex.x, vertex.y, vertex.z).normalize();
                const position = direction.clone().multiplyScalar(radius + height / 2);
                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);
                scene.add(cone);
                coneMeshes.push(cone);
            });
        }

        createSphere(0.75);
        createCones(currentVertices, currentRadius, currentHeight, currentBase);

        // Polyhedron vertices
        const geometries = {
            'Cube': new THREE.BoxGeometry(1, 1, 1),
            'Dodecahedron': new THREE.DodecahedronGeometry(1),
            'Icosahedron': new THREE.IcosahedronGeometry(1),
            'Octahedron': new THREE.OctahedronGeometry(1),
            'Tetrahedron': new THREE.TetrahedronGeometry(1)
        };

        function updatePolyhedron(buttonId) {
            const button = document.getElementById(buttonId);
            const polyhedron = buttonId.replace('btn-', '');
            currentVertices = [];
            currentRadius = 1;
            if (geometries[polyhedron]) {
                const geometry = geometries[polyhedron];
                currentVertices = Array.from({ length: geometry.attributes.position.count / 3 }).map((_, index) => {
                    const vertexIndex = index * 3;
                    return {
                        x: geometry.attributes.position.array[vertexIndex],
                        y: geometry.attributes.position.array[vertexIndex + 1],
                        z: geometry.attributes.position.array[vertexIndex + 2]
                    };
                });
            }
            clearActiveState();
            button.classList.add('btn-active');
            createCones(currentVertices, currentRadius, currentHeight, currentBase);
        }

        function clearActiveState() {
            buttons.forEach(button => button.classList.remove('btn-active'));
        }

        const buttons = document.querySelectorAll('#polyhedron-buttons .btn');
        buttons.forEach(button => button.addEventListener('click', function() {
            updatePolyhedron(this.id);
        }));

        // Event listeners for range sliders
        document.getElementById('slider-sphere-diameter').addEventListener('input', function() {
            const newRadius = this.value * 0.5;
            sphereMesh.scale.set(newRadius, newRadius, newRadius);
            currentRadius = newRadius;
            updateConePositions();
        });

        document.getElementById('slider-cone-height').addEventListener('input', function() {
            currentHeight = this.value;
            updateConeHeights();
        });

        document.getElementById('slider-cone-base').addEventListener('input', function() {
            currentBase = this.value;
            updateConeBases();
        });

        function updateConePositions() {
            createCones(currentVertices, currentRadius, currentHeight, currentBase);
        }

        function updateConeHeights() {
            coneMeshes.forEach(cone => {
                cone.geometry.dispose();
                cone.geometry = new THREE.ConeGeometry(currentBase, currentHeight, 32);
            });
            updateConePositions();
        }

        function updateConeBases() {
            coneMeshes.forEach(cone => {
                cone.geometry.dispose();
                cone.geometry = new THREE.ConeGeometry(currentBase, currentHeight, 32);
            });
            updateConePositions();
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        render();
    </script>
</body>
</html>
