import React, { useState, useEffect } from 'react';
import './TakeToTopButton.css';

const TakeToTopButton = () => {
  const [isVisible, setIsVisible] = useState(false);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Toggle the button's visibility based on scroll position
  useEffect(() => {
    const toggleVisibility = () => {
      const scrollTop = document.documentElement.scrollTop || document.body.scrollTop;
      return !scrollTop > 100;
    };

    window.addEventListener('scroll', () => {
      if (isVisible !== toggleVisibility()) setIsVisible(toggleVisibility());
    });
  }, [isVisible]);

  return (
    <button
      className={`takeToTopButton ${isVisible ? '' : ' hidden'}`}
      onClick={scrollToTop}
      title="Take To Top"
    ></button>
  );
};

export default TakeToTopButton;
