  let Q = 2,
      q = -0.0845,
      x = 2,
      y = 3,
      k = 1,
      scale = 40; // pixels per meter

  function setup() {
    createCanvas(600, 500);
    noLoop();
    setupCanvas();
    updateDisplay();
  }

  function draw() {
    background(220);
    // Grid and axes are drawn in setup() since they don't change
    // Draw the source charge at the origin
    fill(0, 0, 255);
    ellipse(0, 0, 10 * scale, 10 * scale);
    // Draw the test charge locator
    fill(0);
    ellipse(x * scale, -y * scale, 10 * scale, 10 * scale);
    // Draw electric field vector
    let E_mag = k * Q / ((x**2 + y**2) ** 0.5);
    let Ex = k * Q * x / ((x**2 + y**2) ** 0.5) ** 2;
    let Ey = k * Q * y / ((x**2 + y**2) ** 0.5) ** 2;
    let force = q * E_mag;
    let Fx = q * Ex;
    let Fy = q * Ey;
    drawArrow(x * scale, -y * scale, x * scale + Ex * scale, -y * scale + Ey * scale, color(255, 0, 0));
    if (q !== 0) {
      drawArrow(x * scale, -y * scale, x * scale + Fx * scale, -y * scale + Fy * scale, color(0, 0, 255));
    }
  }

  function drawArrow(baseX, baseY, endX, endY, color) {
    stroke(color);
    line(baseX, baseY, endX, endY);
    push();
    translate(endX, endY);
    rotate(-atan2(endY - baseY, endX - baseX));
    beginShape();
    vertex(0, -10);
    bezierVertex(-10, 50, 10, 50, 0, -10);
    endShape();
    pop();
  }

  function setupCanvas() {
    translate(width / 2, height / 2);
    scale(1, -1);
    grid(7, 2, 0.5);
    // Draw the grid and axes (major every 2, minor every 0.5, y-axis inverted)
  }

  function grid(maxLen, interval, minorInterval) {
    stroke(150);
    // Draw vertical lines, y-axis inverted
    for (let i = -maxLen * scale; i <= maxLen * scale; i += interval * scale) {
      if (i % minorInterval * scale === 0) {
        strokeWeight(1);
      } else {
        strokeWeight(0.5);
      }
      line(i, -maxLen * scale, i, maxLen * scale);
      text((i / scale).toFixed(1), i - 5, 0); // Align to the left of the tick marks
    }
    // Draw horizontal lines
    for (let j = -maxLen * scale; j < maxLen * scale; j += interval * scale) {
      if (j % minorInterval * scale === 0) {
        strokeWeight(1);
      } else {
        strokeWeight(0.5);
      }
      line(-maxLen * scale, j, maxLen * scale, j);
      text((j / scale).toFixed(1), 0, j + 10); // Align above the tick marks
    }
  }

