            const E_plus = k_values.map(k => {
                const term = (BX ** 2 + BY ** 2 + (BZ + wso * k) ** 2);
                return (2 * n + 1 + k ** 2  + Math.sqrt(term)) / 1
            });
            const E_minus = k_values.map(k => {
                const term = (BX ** 2 + BY ** 2 + (BZ + wso * k) ** 2);
                return (2 * n + 1 + k ** 2  - Math.sqrt(term)) / 1;
            });

            // Determine if all parameters are zero
            const all_zero = wso === 0 && BX === 0 && BY === 0 && BZ === 0;
            const plotData = [];

            if (all_zero) {
                // Only one trace if all parameters are zero
                plotData.push({
