                    y: E_plus.map(e => e - 20),
                    mode: 'lines',
                    line: { color: 'red', dash: 'solid', width: 2 }
                });
            } else {
                // Two traces for the remaining and minus cases
                plotData.push({
                    x: k_values,
                    y: E_plus,
                    mode: 'lines',
                    line: { color: 'red', dash: 'solid', width: 2 }
                });
                plotData.push({
                    x: k_values,
                    y: E_minus,
                    mode: 'lines',
                    line: { color: 'red', dash: 'dash', width: 2 }
                });
            }

            // Create the plot's dynamic title
            let title = `n=${n}`;
            if (wso !== 0) title += `, WSO=${wso.toFixed(1)}`;
            if (BX !== 0) title += `, BX=${BX.toFixed(1)}`;
            if (BY !== 0) title += `, BY=${BY.toFixed(1)}`;
            if (BZ !== 0) title += `, BZ=${BZ.toFixed(1)}`;

            // Update the Plotly plot with new data and layout
            Plotly.react('plot', plotData, plotLayout, { title });
        }

        // Set up event listeners for slider interactions
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('change', function() {
                updatePlot();
            });
        });

        // Initialize the initial plot when the page loads with default values
        updatePlot();
    </script>
