                document.getElementById("value-b0").textContent = b_0.toFixed(1);
                document.getElementById("value-r").textContent = r.toFixed(3);
                document.getElementById("value-beta").textContent = beta.toFixed(3);

                // Calculations for the Richards and 4/5 Law curves
                const t_values = Array.from({length: 66}, (_, i) => 15 + i); // ages from 15 to 80
                const V_t_values = t_values.map(t => {
                    const M = (b_max / b_0) ** beta - 1;
                    return b_max / (1 + M * Math.exp(-r * t)) ** (1 / beta);
                });

                // Calculations for the 4/5 Law parameters
                let u, p;
                try {
                    const t_i = Math.log((Math.pow(b_max / b_0, beta) - 1) * beta) / r;
                    const V_i = b_max * (Math.pow(beta + 1, 1 / beta) / (2 * beta));
                    const V_i_prime = (r / beta) * V_i * (1 - Math.pow(V_i / b_max, beta));
                    u = t_i - (4 / 5) * (V_i / V_i_prime);
                    p = V_i / Math.pow(t_i - u, 4 / 5);
                } catch (e) {
                    throw new Error(`Derivation failed at t_i=${t_i}, V_i=${V_i}`);
                }

                // Calculate the 4/5 Law curve
                const V_4_5_values = V_i >= 0 ?
                    t_values.map(t => p * Math.pow(t - u, 4 / 5)) :
                    Array.from({length: t_values.length}, () => undefined);

                // Update the plot
                Plotly.restyle("plot-container", "x", [t_values, t_values]);
                Plotly.restyle("plot-container", "y", [[...V_t_values, ...V_4_5_values.slice(0, t_values.length)], V_4_5_values.slice(t_values.length)]);
                Plotly.restyle("plot-container", "type", ["scatter", undefined]); // Discard non-data points
                Plotly.restyle("plot-container", "line", [
                    {color: 'orange'},
                    {color: 'blue'}
                ]);
                Plotly.restyle("plot-container", "mode", ["lines", undefined]);

                // Update results display
                document.getElementById("results-display").innerHTML = `
                    <p>rejuvenation bias u = ${u.toFixed(4)}</p>
                    <p>site productivity p = ${p.toFixed(4)}</p>
                `;
            }

            // Attach event listeners to sliders
            document.getElementById("slider-bmax").addEventListener("input", update);
            // Repeat for other sliders
        });
    </script>
