    function draw() {
        background(240);

        // Orbit Sketch
        let a = width * 0.4;
        let b = a * sqrt(1 - pow(eccentricity, 2));
        push();
        translate(width / 2, height / 2);
        rotate(radians(longitude));
        ellipse(a, 0, a * 2, b * 2);
        pop();

        // Equation of Time Plot
        let plotCanvas = createCanvas(windowWidth, height / 2);
        plotCanvas.parent('plot-canvas');
        background(255);
        stroke(0);
        fill(192, 82, 45);
        line(0, height / 2, width, height / 2);

        let values = [];
        for (let M = 0; M <= TWO_PI; M += 0.1) {
            let E = M - eccentricity * sin(M);
            for (let i = 0; i < 10; i++) {
                let E_new = E - (E - eccentricity * sin(E) - M) / (1 - eccentricity * cos(E));
                if (abs(E_new - E) < 0.0001) break;
                E = E_new;
            }
            let v = 2 * atan2(sqrt(1 + eccentricity) * sin(E / 2), sqrt(1 - eccentricity) * cos(E / 2));
            let alpha = atan2(cos(radians(angle)) * sin(v + radians(longitude)), cos(v + radians(longitude)));
            let EOT_rad = M - alpha;
            if (abs(EOT_rad - values[values.length - 1]) > PI) {
                EOT_rad += (EOT_rad > values[values.length - 1] ? 2 * PI : -2 * PI);
            }
            values.push({ x: M, y: EOT_rad * (720 / PI) });
        }

        let minEOT = min(values, el => el.y);
        let maxEOT = max(values, el => el.y);
        let scaleY = height / 2 / (maxEOT - minEOT);

        stroke(255, 87, 34);
        beginShape();
        values.forEach(val => vertex(val.x, height / 2 - val.y * scaleY));
        endShape();
    }

    function updateValues() {
        angle = parseFloat(document.getElementById('value-angle').innerText);
        longitude = parseFloat(document.getElementById('value-longitude').innerText);
        eccentricity = parseFloat(document.getElementById('value-eccentricity').innerText);
        redraw();
    }

    document.querySelectorAll('input[type="range"]').forEach(slider => {
        slider.addEventListener('input', () => {
            let span = document.getElementById(slider.id.replace('slider-', 'value-'));
            span.innerText = slider.value;
            updateValues();
        });
    });
</script>
