<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="shift-slider">shift:</label>
            <input type="range" id="shift-slider" min="0" max="25" value="12" step="1">
            <input type="text" id="text-input" list="phrase-list" value="abcd">
            <datalist id="phrase-list">
                <option value="abcd">
                <option value="test phrase">
                <option value="to be or not to be">
                <option value="the quick brown fox jumped over the lazy dog">
            </datalist>
        </div>

        <div id="display-area">
            <div class="alphabet-container">
                <div id="plaintext-alphabet" class="alphabet-row"></div>
                <div id="ciphertext-alphabet" class="alphabet-row"></div>
            </div>
            <div id="key-section">
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>
            <div id="text-section">
                <p id="plaintext-output"><span class="text-label">plaintext : </span>abcd</p>
                <p id="ciphertext-output"><span class="text-label">ciphertext: </span>MNOP</p>
            </div>
        </div>
    </div>

    <script>
        function encrypt(text, shift) {
            return text.toLowerCase().split('').map(char => {
                if (char >= 'a' && char <= 'z') {
                    const code = ((char.charCodeAt(0) - 97 + shift) % 26) + 65;
                    return String.fromCharCode(code);
                }
                return char;
            }).join('');
        }

        function updateCipher() {
            const shift = parseInt(document.getElementById('shift-slider').value);
            const text = document.getElementById('text-input').value;

            // Update alphabet display
            const plainAlpha = document.getElementById('plaintext-alphabet');
            const cipherAlpha = document.getElementById('ciphertext-alphabet');
            plainAlpha.innerHTML = '';
            cipherAlpha.innerHTML = '';

            for (let i = 0; i < 26; i++) {
                const plainChar = String.fromCharCode(97 + i);
                const cipherChar = String.fromCharCode(65 + ((i + shift) % 26));

                plainAlpha.innerHTML += `<span class="letter-span">${plainChar}</span>`;
                cipherAlpha.innerHTML += `<span class="letter-span">${cipherChar}</span>`;
            }

            // Update keys
            document.getElementById('encryption-key-display').textContent = `encryption key: ${shift}`;
            const decryptKey = (26 - shift) % 26;
            document.getElementById('decryption-key-display').textContent = `decryption key: ${decryptKey}`;

            // Update text output
            document.getElementById('plaintext-output').innerHTML = 
                `<span class="text-label">plaintext : </span>${text}`;
            document.getElementById('ciphertext-output').innerHTML = 
                `<span class="text-label">ciphertext: </span>${encrypt(text, shift)}`;
        }

        // Event listeners
        document.getElementById('shift-slider').addEventListener('input', updateCipher);
        document.getElementById('text-input').addEventListener('input', updateCipher);

        // Initial update
        updateCipher();
    </script>
</body>
</html>
