<!DOCTYPE html>
<html>
<head>
    <title>Ionic Coordination Viewer</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <style>
        body {
            margin: 0;
            padding: 20px;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            margin-bottom: 20px;
        }
        .control-group {
            margin: 10px 0;
        }
        .control-group label {
            display: inline-block;
            width: 150px;
            margin-right: 10px;
        }
        button {
            padding: 5px 15px;
            margin-right: 5px;
            cursor: pointer;
        }
        button.active {
            background-color: #4CAF50;
            color: white;
            border: none;
        }
        #canvas-container {
            width: 800px;
            height: 600px;
            border: 1px solid #ccc;
        }
        #ratio-text {
            padding: 10px;
            text-align: center;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label>coordination:</label>
                <button id="btn-threefold" class="active">threefold</button>
                <button id="btn-fourfold">fourfold</button>
                <button id="btn-sixfold">sixfold</button>
                <button id="btn-eightfold">eightfold</button>
            </div>
            <div class="control-group">
                <label>relative anion radius:</label>
                <input type="range" id="slider-anion-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                <span id="value-anion-radius">0.100</span>
            </div>
            <div class="control-group">
                <label>relative cation radius:</label>
                <input type="range" id="slider-cation-radius" min="0.1" max="1.0" step="0.001" value="0.1">
                <span id="value-cation-radius">0.100</span>
            </div>
            <div class="control-group">
                <label>opacity of anions:</label>
                <input type="range" id="slider-opacity" min="0.0" max="1.0" step="0.01" value="0.8">
            </div>
        </div>
        <div id="canvas-container"></div>
        <p id="ratio-text">The ratio of the cation radius to the anion radius is 1.00.</p>
    </div>

    <script>
        let scene, camera, renderer, controls;
        let cation, anions = [], boundingBox;
        let currentCoordination = 'threefold';

        // Initialize Three.js scene
        function init() {
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, 800/600, 0.1, 1000);
            renderer = new THREE.WebGLRenderer();
            renderer.setSize(800, 600);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Camera and controls
            camera.position.z = 5;
            controls = new THREE.OrbitControls(camera, renderer.domElement);

            createScene();
            animate();
        }

        function createScene() {
            // Clear existing objects
            while(anions.length > 0) {
                scene.remove(anions.pop());
            }
            if(cation) scene.remove(cation);
            if(boundingBox) scene.remove(boundingBox);

            // Create cation
            const cationGeometry = new THREE.SphereGeometry(
                parseFloat(document.getElementById('slider-cation-radius').value), 32, 32
            );
            const cationMaterial = new THREE.MeshPhongMaterial({color: 0x4444ff});
            cation = new THREE.Mesh(cationGeometry, cationMaterial);
            scene.add(cation);

            // Create anions
            const anionRadius = parseFloat(document.getElementById('slider-anion-radius').value);
            const anionOpacity = parseFloat(document.getElementById('slider-opacity').value);
            const anionMaterial = new THREE.MeshPhongMaterial({
                color: 0xe0d8e0,
                transparent: true,
                opacity: anionOpacity
            });

            let positions = [];
            const distance = 1.5;

            switch(currentCoordination) {
                case 'threefold':
                    for(let i = 0; i < 3; i++) {
                        const angle = (i * 2 * Math.PI) / 3;
                        positions.push(new THREE.Vector3(
                            distance * Math.cos(angle),
                            distance * Math.sin(angle),
                            0
                        ));
                    }
                    break;
                case 'fourfold':
                    positions = [
                        new THREE.Vector3(distance, distance, distance),
                        new THREE.Vector3(-distance, -distance, distance),
                        new THREE.Vector3(-distance, distance, -distance),
                        new THREE.Vector3(distance, -distance, -distance)
                    ];
                    break;
                case 'sixfold':
                    positions = [
                        new THREE.Vector3(distance, 0, 0),
                        new THREE.Vector3(-distance, 0, 0),
                        new THREE.Vector3(0, distance, 0),
                        new THREE.Vector3(0, -distance, 0),
                        new THREE.Vector3(0, 0, distance),
                        new THREE.Vector3(0, 0, -distance)
                    ];
                    break;
                case 'eightfold':
                    for(let x of [-1, 1]) {
                        for(let y of [-1, 1]) {
                            for(let z of [-1, 1]) {
                                positions.push(new THREE.Vector3(
                                    x * distance,
                                    y * distance,
                                    z * distance
                                ));
                            }
                        }
                    }
                    break;
            }

            positions.forEach(pos => {
                const anionGeometry = new THREE.SphereGeometry(anionRadius, 32, 32);
                const anion = new THREE.Mesh(anionGeometry, anionMaterial);
                anion.position.copy(pos);
                scene.add(anion);
                anions.push(anion);
            });

            if(currentCoordination !== 'threefold') {
                const boxGeometry = new THREE.BoxGeometry(
                    distance * 2.2,
                    distance * 2.2,
                    distance * 2.2
                );
                const edges = new THREE.EdgesGeometry(boxGeometry);
                boundingBox = new THREE.LineSegments(
                    edges,
                    new THREE.LineBasicMaterial({color: 0x888888})
                );
                scene.add(boundingBox);
            }

            updateRatioText();
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        function updateRatioText() {
            const cationRadius = parseFloat(document.getElementById('slider-cation-radius').value);
            const anionRadius = parseFloat(document.getElementById('slider-anion-radius').value);
            const ratio = (cationRadius / anionRadius).toFixed(2);
            document.getElementById('ratio-text').textContent =
                `The ratio of the cation radius to the anion radius is ${ratio}.`;
        }

        // Event listeners
        document.querySelectorAll('button').forEach(button => {
            button.addEventListener('click', function() {
                document.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                currentCoordination = this.id.replace('btn-', '');
                createScene();
            });
        });

        ['anion-radius', 'cation-radius', 'opacity'].forEach(id => {
            const slider = document.getElementById(`slider-${id}`);
            slider.addEventListener('input', function() {
                if(id !== 'opacity') {
                    document.getElementById(`value-${id}`).textContent =
                        this.value.padEnd(5, '0');
                }
                createScene();
            });
        });

        // Initialize the scene
        init();
    </script>
</body>
</html>
