            width: 40px;
            height: 40px;
            font-size: 20px;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div id="slider-container">
            <label for="slider-angle">slope θ</label>
            <input type="range" id="slider-angle" min="10" max="80" value="30" step="1">
        </div>
        <div id="animation-controls">
            <p>release block</p>
            <button id="btn-play">▶</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|<</button>
        </div>
    </div>
    <div id="visualization-area">
        <p id="info-display">slope = \( \theta \) = 30°; acceleration = \( g \sin(\theta) \) = 4.905 m/s²</p>
        <div id="canvas-container"></div>
    </div>

    <script>
        let angle = 30;
        let isPlaying = false;
        let blockPos = 0;
        let startTime = 0;
        const g = 9.81;

        function updateInfoDisplay() {
            const acc = (g * Math.sin(angle * Math.PI / 180)).toFixed(3);
            document.getElementById('info-display').innerHTML = 
                `slope = \\( \\theta \\) = ${angle}°; acceleration = \\( g \\sin(\\theta) \\) = ${acc} m/s²`;
            MathJax.typeset();
        }

        // p5.js setup and draw functions
        function setup() {
            const canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
        }

        function draw() {
            background(255);
            translate(width/3, height*2/3);

            // Draw inclined plane
            push();
            fill(200, 230, 255);
            stroke(0);
            const baseLength = 300;
            const height = baseLength * Math.tan(angle * Math.PI / 180);
            triangle(0, 0, baseLength, 0, 0, -height);
            pop();

            // Calculate block position
            let distanceAlongSlope = 0;
            if (isPlaying) {
                const elapsed = (millis() - startTime) / 1000;
                const acc = g * Math.sin(angle * Math.PI / 180);
                distanceAlongSlope = 0.5 * acc * elapsed * elapsed;
            }

            // Draw block
            push();
            const slopeLength = sqrt(baseLength*baseLength + height*height);
            const blockSize = 30;
            const blockX = distanceAlongSlope * cos(angle * PI/180);
            const blockY = -height + distanceAlongSlope * sin(angle * PI/180);
            translate(blockX, blockY);
            rotate(-angle * PI/180);
            fill(100);
            rect(-blockSize/2, -blockSize/2, blockSize, blockSize);
            pop();
        }

        // Event listeners
        document.getElementById('slider-angle').addEventListener('input', function(e) {
            angle = parseInt(e.target.value);
            isPlaying = false;
            blockPos = 0;
            updateInfoDisplay();
        });

        document.getElementById('btn-play').addEventListener('click', function() {
            isPlaying = true;
            startTime = millis();
        });

        document.getElementById('btn-pause').addEventListener('click', function() {
            isPlaying = false;
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            isPlaying = false;
            blockPos = 0;
            startTime = millis();
        });

        // Initial MathJax rendering
        MathJax.typeset();
    </script>
</body>
</html>
