<!DOCTYPE html>
<html>
<head>
    <title>Sorting Algorithm Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 20px;
        }
        #control-panel {
            display: flex;
            flex-direction: column;
            gap: 15px;
            margin-bottom: 20px;
        }
        #elements-control, #step-control, #sort-control {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        .sort-btn {
            padding: 5px 10px;
            cursor: pointer;
        }
        .sort-btn.active {
            background-color: #007bff;
            color: white;
        }
        button {
            padding: 5px 10px;
            cursor: pointer;
        }
        #btn-randomize {
            margin-left: 20px;
            background-color: #28a745;
            color: white;
            border: none;
            padding: 5px 15px;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <div id="elements-control">
            <label for="slider-elements">elements (set, then click "randomize")</label>
            <input type="range" id="slider-elements" min="2" max="50" value="10" step="1">
            <span id="elements-value">10</span>
        </div>
        <div id="step-control">
            <label for="slider-step">step</label>
            <input type="range" id="slider-step" min="1" value="1" step="1">
            <button id="btn-play-pause">▶</button>
            <button id="btn-step-backward">-</button>
            <button id="btn-step-forward">+</button>
            <button id="btn-reset-step">«</button>
            <span id="step-value">1</span>
        </div>
        <div id="sort-control">
            <label>sort</label>
            <button id="btn-shellsort" class="sort-btn active">shellsort</button>
            <button id="btn-bubblesort" class="sort-btn">bubblesort</button>
            <button id="btn-insertionsort" class="sort-btn">insertionsort</button>
            <button id="btn-cocktail" class="sort-btn">cocktail</button>
            <button id="btn-bogosort" class="sort-btn">bogosort</button>
            <button id="btn-randomize">randomize</button>
        </div>
    </div>
    <div id="visualization-area">
        <div id="bar-chart-canvas"></div>
    </div>

    <script>
        let state = {
            currentAlgorithm: 'shellsort',
            numElements: 10,
            sortHistory: [],
            currentStep: 1,
            isPlaying: false,
            animationTimer: null
        };

        let margin = 50;
        let canvas;

        function setup() {
            canvas = createCanvas(800, 400);
            canvas.parent('bar-chart-canvas');
            colorMode(HSB);
            noLoop();
            initializeControls();
            randomizeArray();
        }

        function draw() {
            background(255);
            drawAxis();
            drawBars();
        }

        function drawAxis() {
            stroke(0);
            line(margin, height - margin, margin, margin);
            line(margin, height - margin, width - margin, height - margin);

            // Y-axis labels
            for(let i = 0; i <= state.numElements; i += 5) {
                let y = map(i, 0, state.numElements, height - margin, margin);
                line(margin - 5, y, margin, y);
                textAlign(RIGHT, CENTER);
                text(i, margin - 10, y);
            }
        }

        function drawBars() {
            if (!state.sortHistory.length) return;

            let currentArray = state.sortHistory[state.currentStep];
            let barWidth = (width - 2 * margin) / state.numElements;

            for(let i = 0; i < currentArray.length; i++) {
                let value = currentArray[i];
                let x = margin + i * barWidth;
                let h = map(value, 0, state.numElements, 0, height - 2 * margin);
                let hue = map(value, 1, state.numElements, 240, 0);

                fill(hue, 100, 100);
                stroke(0);
                rect(x, height - margin - h, barWidth - 1, h);
            }
        }

        function initializeControls() {
            // Elements slider
            document.getElementById('slider-elements').addEventListener('input', function(e) {
                state.numElements = parseInt(e.target.value);
                document.getElementById('elements-value').textContent = state.numElements;
            });

            // Sort buttons
            document.querySelectorAll('.sort-btn').forEach(btn => {
                btn.addEventListener('click', function() {
                    document.querySelectorAll('.sort-btn').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    state.currentAlgorithm = this.id.replace('btn-', '');

                    if(state.currentAlgorithm === 'bogosort') {
                        document.getElementById('slider-elements').value = 7;
                        document.getElementById('slider-elements').disabled = true;
                        state.numElements = 7;
                        document.getElementById('elements-value').textContent = 7;
                    } else {
                        document.getElementById('slider-elements').disabled = false;
                    }

                    randomizeArray();
                });
            });

            // Randomize button
            document.getElementById('btn-randomize').addEventListener('click', randomizeArray);

            // Step controls
            document.getElementById('btn-play-pause').addEventListener('click', togglePlay);
            document.getElementById('btn-step-forward').addEventListener('click', stepForward);
            document.getElementById('btn-step-backward').addEventListener('click', stepBackward);
            document.getElementById('btn-reset-step').addEventListener('click', resetStep);
        }

        function randomizeArray() {
            let array = Array.from({length: state.numElements}, (_, i) => i + 1);
            for(let i = array.length - 1; i > 0; i--) {
                let j = Math.floor(Math.random() * (i + 1));
                [array[i], array[j]] = [array[j], array[i]];
            }

            state.sortHistory = [array];
            generateSortHistory(array.slice());

            state.currentStep = 1;
            document.getElementById('slider-step').value = 1;
            document.getElementById('slider-step').max = state.sortHistory.length - 1;
            document.getElementById('step-value').textContent = 1;

            stopAnimation();
            redraw();
        }

        function generateSortHistory(array) {
            // Simplified example using bubble sort
            // In a real implementation, this would switch between different sorting algorithms
            let history = [array.slice()];

            for(let i = 0; i < array.length; i++) {
                for(let j = 0; j < array.length - i - 1; j++) {
                    if(array[j] > array[j + 1]) {
                        [array[j], array[j + 1]] = [array[j + 1], array[j]];
                        history.push(array.slice());
                    }
                }
            }

            state.sortHistory = history;
        }

        function togglePlay() {
            if(state.isPlaying) {
                stopAnimation();
            } else {
                startAnimation();
            }
        }

        function startAnimation() {
            state.isPlaying = true;
            document.getElementById('btn-play-pause').textContent = '❚❚';
            state.animationTimer = setInterval(() => {
                if(state.currentStep >= state.sortHistory.length - 1) {
                    stopAnimation();
                    return;
                }
                stepForward();
            }, 200);
        }

        function stopAnimation() {
            state.isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
            if(state.animationTimer) clearInterval(state.animationTimer);
        }

        function stepForward() {
            if(state.currentStep < state.sortHistory.length - 1) {
                state.currentStep++;
                updateStepControls();
                redraw();
            }
        }

        function stepBackward() {
            if(state.currentStep > 1) {
                state.currentStep--;
                updateStepControls();
                redraw();
            }
        }

        function resetStep() {
            state.currentStep = 1;
            updateStepControls();
            redraw();
        }

        function updateStepControls() {
            document.getElementById('slider-step').value = state.currentStep;
            document.getElementById('step-value').textContent = state.currentStep;
        }
    </script>
</body>
</html>
