<body>
    <div id="demo-container">
        <div id="control-panel">
            <button id="btn-reset">↺</button>
            <div class="slider-row">
                <label for="slider-x1">x₁</label>
                <input type="range" id="slider-x1" min="-1000" max="1000" step="1" value="500">
                <span id="value-x1">500</span>
            </div>
            <div class="slider-row">
                <label for="slider-v1">v₁</label>
                <input type="range" id="slider-v1" min="-15" max="15" step="0.01" value="-10">
                <span id="value-v1">-10.00</span>
            </div>
            <div class="slider-row">
                <label for="slider-x2">x₂</label>
                <input type="range" id="slider-x2" min="-1000" max="1000" step="1" value="100">
                <span id="value-x2">100</span>
            </div>
            <div class="slider-row">
                <label for="slider-v2">v₂</label>
                <input type="range" id="slider-v2" min="-15" max="15" step="0.01" value="5">
                <span id="value-v2">5.00</span>
            </div>
        </div>
        <div id="visualization-area">
            <h2 id="result-text"></h2>
            <div id="plot-container"></div>
        </div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        const sliders = {
            x1: document.getElementById('slider-x1'),
            v1: document.getElementById('slider-v1'),
            x2: document.getElementById('slider-x2'),
            v2: document.getElementById('slider-v2')
        };

        const valueDisplays = {
            x1: document.getElementById('value-x1'),
            v1: document.getElementById('value-v1'),
            x2: document.getElementById('value-x2'),
            v2: document.getElementById('value-v2')
        };

        const resultText = document.getElementById('result-text');
        const resetBtn = document.getElementById('btn-reset');

        function calculateIntersection() {
            const x1 = parseFloat(sliders.x1.value);
            const v1 = parseFloat(sliders.v1.value);
            const x2 = parseFloat(sliders.x2.value);
            const v2 = parseFloat(sliders.v2.value);

            if (Math.abs(v1 - v2) < 0.001) {
                return { intersects: false };
            }

            const T = (x2 - x1) / (v1 - v2);
            const X = x1 + v1 * T;

            return {
                intersects: T >= 0 && T <= 1000,
                T: T,
                X: X
            };
        }

        function updatePlot() {
            const x1 = parseFloat(sliders.x1.value);
            const v1 = parseFloat(sliders.v1.value);
            const x2 = parseFloat(sliders.x2.value);
            const v2 = parseFloat(sliders.v2.value);

            const intersection = calculateIntersection();

            const trace1 = {
                x: [0, 1000],
                y: [x1, x1 + v1 * 1000],
                type: 'scatter',
                mode: 'lines',
                line: { color: 'rgb(70, 130, 180)' },
                name: 'Runner 1'
            };

            const trace2 = {
                x: [0, 1000],
                y: [x2, x2 + v2 * 1000],
                type: 'scatter',
                mode: 'lines',
                line: { color: 'rgb(255, 165, 0)' },
                name: 'Runner 2'
            };

            const traces = [trace1, trace2];

            if (intersection.intersects) {
                traces.push({
                    x: [intersection.T],
                    y: [intersection.X],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        size: 10,
                        color: 'rgb(50, 205, 50)',
                        line: { color: 'rgb(34, 139, 34)', width: 2 }
                    },
                    name: 'Intersection'
                });
                resultText.textContent = `T = ${intersection.T.toFixed(2)} s | X = ${intersection.X.toFixed(2)} m`;
            } else {
                resultText.textContent = "The runners' paths never intersect";
            }

            const layout = {
                showlegend: false,
                xaxis: { title: 't', range: [0, 1000] },
                yaxis: { title: 'distance', autorange: true },
                margin: { t: 20, r: 20, b: 40, l: 60 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white',
                grid: { color: '#eee' }
            };

            Plotly.react('plot-container', traces, layout);
        }

        function updateValueDisplays() {
            valueDisplays.x1.textContent = sliders.x1.value;
            valueDisplays.v1.textContent = parseFloat(sliders.v1.value).toFixed(2);
            valueDisplays.x2.textContent = sliders.x2.value;
            valueDisplays.v2.textContent = parseFloat(sliders.v2.value).toFixed(2);
        }

        function reset() {
            sliders.x1.value = 500;
            sliders.v1.value = -10;
            sliders.x2.value = 100;
            sliders.v2.value = 5;
            updateValueDisplays();
            updatePlot();
        }

        // Event listeners
        Object.values(sliders).forEach(slider => {
            slider.addEventListener('input', () => {
                updateValueDisplays();
                updatePlot();
            });
        });

        resetBtn.addEventListener('click', reset);

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
