            <span id="slider-value">0</span>
            <button id="btn-reset">↻</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let tooltips = {
            'Clouds': 'Water vapor condensed in the atmosphere.',
            'Rocks': 'A primary reservoir of phosphorus in the form of phosphate.',
            'Animals': 'Obtain phosphorus by eating plants or other animals.',
            'Plants': 'Absorb phosphate from the soil to build organic molecules.',
            'Water': 'Phosphorus is carried into water bodies by runoff and leaching.',
            'Erosion': 'Weathering of rocks by wind and water releases phosphate.',
            'Runoff': 'Phosphate from fertilizers and rocks is washed into rivers and oceans.',
            'Soil': 'Phosphorus in the soil is available for plants.',
            'Leaching': 'Phosphates in the soil are dissolved in water and carried away.',
            'Plant uptake': 'Plants absorb inorganic phosphate from the soil through their roots.',
            'Animals eat plants': 'Transfers phosphorus from plants to animals along the food chain.',
            'Decomposition': 'Bacteria break down dead organic matter, returning phosphate to the soil and water.',
            'Geologic uplift': 'Over millions of years, ocean sediments containing phosphate are lifted up to form new land.'
        };

        let labels = [];
        let currentPhase = 0;

        function setup() {
            let canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            textAlign(CENTER, CENTER);
        }

        function drawArrow(x1, y1, x2, y2) {
            line(x1, y1, x2, y2);
            push();
            translate(x2, y2);
            let angle = atan2(y2 - y1, x2 - x1);
            rotate(angle);
            triangle(-15, -5, -15, 5, 0, 0);
            pop();
        }

        function draw() {
            background(255);

            // Sky
            fill('#CAE9FF');
            noStroke();
            rect(0, 0, width, height/2);

            // Ground
            fill('#9B7653');
            rect(0, height/2, width, height/2);

            // Sun
            fill('#FFDD00');
            circle(500, 80, 60);

            // Clouds
            fill('#E0E0E0');
            ellipse(150, 80, 100, 60);
            ellipse(180, 70, 80, 50);
            text('Clouds', 150, 80);
            labels.push({x: 150, y: 80, w: 60, h: 30, text: 'Clouds'});

            // Rain
            stroke('#0000FF');
            for(let i = 0; i < 5; i++) {
                line(120 + i*15, 100, 110 + i*15, 130);
            }

            // Rocks
            fill('#5A5A5A');
            noStroke();
            ellipse(200, 300, 80, 60);
            ellipse(170, 320, 60, 40);
            text('Rocks', 200, 350);
            labels.push({x: 200, y: 350, w: 60, h: 30, text: 'Rocks'});

            // Water
            fill(0, 0, 255, 150);
            ellipse(100, 400, 120, 80);
            text('Water', 100, 400);
            labels.push({x: 100, y: 400, w: 60, h: 30, text: 'Water'});

            // Plants
            fill('#90EE90');
            triangle(400, 300, 420, 250, 440, 300);
            text('Plants', 420, 320);
            labels.push({x: 420, y: 320, w: 60, h: 30, text: 'Plants'});

            // Animal
            fill(255);
            stroke(0);
            rect(300, 280, 60, 40);
            ellipse(350, 270, 30, 30);
            text('Animals', 330, 340);
            labels.push({x: 330, y: 340, w: 60, h: 30, text: 'Animals'});

            // Draw process arrows based on slider value
            stroke(0);
            let phase = parseInt(document.getElementById('slider-phase').value);

            if(phase >= 1) {
                drawArrow(150, 130, 200, 280);
                text('Erosion', 160, 200);
                labels.push({x: 160, y: 200, w: 60, h: 30, text: 'Erosion'});
            }

            if(phase >= 2) {
                drawArrow(200, 320, 120, 380);
                text('Runoff', 180, 360);
                labels.push({x: 180, y: 360, w: 60, h: 30, text: 'Runoff'});
            }

            // Add remaining process arrows for phases 3-8 here...

            // Check for hover and display tooltip
            let mouseOver = false;
            for(let label of labels) {
                if(mouseX > label.x - label.w/2 && 
                   mouseX < label.x + label.w/2 &&
                   mouseY > label.y - label.h/2 && 
                   mouseY < label.y + label.h/2) {
                    fill(255, 255, 200);
                    rect(mouseX + 10, mouseY + 10, 200, 30);
                    fill(0);
                    textAlign(LEFT);
                    text(tooltips[label.text], mouseX + 15, mouseY + 25);
                    textAlign(CENTER);
                    mouseOver = true;
                    break;
                }
            }

            labels = []; // Clear labels for next frame
        }

        // Event listeners
        document.getElementById('slider-phase').addEventListener('input', function(e) {
            document.getElementById('slider-value').textContent = e.target.value;
            currentPhase = parseInt(e.target.value);
        });

        document.getElementById('btn-reset').addEventListener('click', function() {
            document.getElementById('slider-phase').value = 0;
            document.getElementById('slider-value').textContent = '0';
            currentPhase = 0;
        });
    </script>
</body>
</html>
