                }
            });

            // Draw matched edges
            stroke(step.isFinal ? '#4d4dff' : '#ff4d4d');
            strokeWeight(6);
            step.matching.forEach(i => {
                const edge = data.edges[i];
                line(data.vertices[edge.u].x, data.vertices[edge.u].y,
                     data.vertices[edge.v].x, data.vertices[edge.v].y);
