                step.blossom.vertices.forEach(i => {
                    vertex(data.vertices[i].x, data.vertices[i].y);
                });
                endShape(CLOSE);

                // Draw blossom super-vertex
                fill(255, 255, 0);
                stroke(0);
                circle(data.vertices[step.blossom.topVertex].x,
                       data.vertices[step.blossom.topVertex].y, 16);
