            flex: 1;
            margin: 0 15px;
        }
        .value-display {
            width: 60px;
            text-align: right;
        }
        #plot-container {
            height: 400px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label>α</label>
                <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
                <span id="value-alpha" class="value-display">0.0500</span>
            </div>
            <div class="control-row">
                <label>β</label>
                <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
                <span id="value-beta" class="value-display">5.00</span>
            </div>
            <div class="control-row">
                <label>γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span id="value-gamma" class="value-display">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        function rk4Step(t, y, h, params) {
            const k1 = derivatives(t, y, params);
            const k2 = derivatives(t + h/2, y.map((yi, i) => yi + k1[i]*h/2), params);
            const k3 = derivatives(t + h/2, y.map((yi, i) => yi + k2[i]*h/2), params);
            const k4 = derivatives(t + h, y.map((yi, i) => yi + k3[i]*h), params);

            return y.map((yi, i) => yi + (h/6)*(k1[i] + 2*k2[i] + 2*k3[i] + k4[i]));
        }

        function derivatives(t, y, params) {
            const [alpha, beta, gamma] = params;
            const deltaT = y[0];
            const cx = y[1];
            const exp_term = Math.exp(deltaT/(1 + deltaT/alpha));

            return [
                gamma * cx * exp_term - beta * deltaT,
                1 - cx * exp_term
            ];
        }

        function solveSystem(params) {
            const h = 0.05;
            const tmax = 20;
            const steps = Math.ceil(tmax/h);

            let t = 0;
            let y = [0, 0];  // Initial conditions

            const tValues = [t];
            const deltaTValues = [y[0]];
            const cxValues = [y[1]];

            for(let i = 0; i < steps; i++) {
                y = rk4Step(t, y, h, params);
                t += h;
                tValues.push(t);
                deltaTValues.push(y[0]);
                cxValues.push(y[1]);
            }

            return [tValues, deltaTValues, cxValues];
        }

        function updatePlot() {
            const alpha = parseFloat(document.getElementById('slider-alpha').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            const gamma = parseFloat(document.getElementById('slider-gamma').value);

            const [t, deltaT, cx] = solveSystem([alpha, beta, gamma]);

            const data = [
                {
                    x: t,
                    y: deltaT,
                    mode: 'lines',
                    line: { color: 'orange', width: 2 }
                },
                {
                    x: t,
                    y: cx.map(v => v * 1000),
                    mode: 'lines',
                    line: { color: 'steelblue', width: 2 }
                }
            ];

            const layout = {
                xaxis: { title: '', range: [0, 20], zeroline: true },
                yaxis: { title: '', range: [0, 100], zeroline: true },
                showlegend: false,
                margin: { l: 50, r: 20, b: 30, t: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white',
                annotations: [
                    {
                        text: 'ΔT',
                        x: -0.05,
                        y: 100,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    },
                    {
                        text: '1000 C<sub>X</sub>',
                        x: -0.05,
                        y: 95,
                        xref: 'paper',
                        yref: 'y',
                        xanchor: 'right',
                        yanchor: 'top',
                        showarrow: false,
                        font: { color: 'purple' }
                    }
                ]
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        // Event listeners for sliders
        document.getElementById('slider-alpha').addEventListener('input', function() {
            document.getElementById('value-alpha').textContent = this.value.padEnd(6, '0');
            updatePlot();
        });

        document.getElementById('slider-beta').addEventListener('input', function() {
            document.getElementById('value-beta').textContent = parseFloat(this.value).toFixed(2);
            updatePlot();
        });

        document.getElementById('slider-gamma').addEventListener('input', function() {
            document.getElementById('value-gamma').textContent = Math.round(this.value);
            updatePlot();
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
