    <script>
        let time = 7.0;
        let particles = [];
        let isTimeSliderDragging = false;
        const GRID_COLS = 10;
        const GRID_ROWS = 6;

        function setup() {
            const canvas = createCanvas(700, 450);
            canvas.parent('canvas-container');

            // Initialize particle grid
            for(let row = 0; row < GRID_ROWS; row++) {
                for(let col = 0; col < GRID_COLS; col++) {
                    const x0 = map(col, 0, GRID_COLS-1, 50, width-50);
                    const y0 = map(row, 0, GRID_ROWS-1, 50, height-50);
                    particles.push({x0, y0});
                }
            }

            // Setup slider events
            document.getElementById('slider-time').addEventListener('input', function() {
                isTimeSliderDragging = true;
                time = parseFloat(this.value);
                document.getElementById('value-time').textContent = time.toFixed(2);
            });
            document.getElementById('slider-time').addEventListener('mouseup', function() {
                isTimeSliderDragging = false;
            });

            const sliders = ['wavelength', 'longitudinal-amplitude', 'latitudinal-amplitude'];
            sliders.forEach(slider => {
                document.getElementById(`slider-${slider}`).addEventListener('input', function() {
                    document.getElementById(`value-${slider}`).textContent = parseFloat(this.value).toFixed(2);
                });
            });
        }

        function draw() {
            // Update time if not dragging time slider
            if (!isTimeSliderDragging) {
                time = (time + 0.03) % 25;
                document.getElementById('slider-time').value = time;
                document.getElementById('value-time').textContent = time.toFixed(2);
            }

            // Get current parameter values
            const wavelength = parseFloat(document.getElementById('slider-wavelength').value);
            const longAmp = parseFloat(document.getElementById('slider-longitudinal-amplitude').value);
            const latAmp = parseFloat(document.getElementById('slider-latitudinal-amplitude').value);

            // Clear canvas
            background('#E0F0FF');

            // Wave parameters
            const k = TWO_PI / wavelength;

            // Calculate current positions
            const currentPositions = [];

            // Draw elliptical paths and calculate positions
            for(let i = 0; i < particles.length; i++) {
                const row = floor(i / GRID_COLS);
                const {x0, y0} = particles[i];

                const ampDecay = exp(-(row / (GRID_ROWS - 1)) * 1.5);
                const ampX = longAmp * ampDecay * 20;
                const ampY = latAmp * ampDecay * 20;

                // Draw elliptical path
                noFill();
                stroke('#8F998F');
                strokeWeight(2);
                ellipse(x0, y0, ampX * 2, ampY * 2);

                // Calculate current position
                const phase = k * x0 - time;
                const dx = ampX * cos(phase);
                const dy = ampY * sin(phase);
                currentPositions.push({x: x0 + dx, y: y0 + dy});
            }

            // Draw connecting lines and particles
            for(let row = 0; row < GRID_ROWS; row++) {
                // Draw dashed lines connecting particles in this row
                stroke('#0000FF');
                strokeWeight(2);
                drawingContext.setLineDash([5, 5]);

                for(let col = 0; col < GRID_COLS-1; col++) {
                    const idx1 = row * GRID_COLS + col;
                    const idx2 = row * GRID_COLS + col + 1;
                    const pos1 = currentPositions[idx1];
                    const pos2 = currentPositions[idx2];
                    line(pos1.x, pos1.y, pos2.x, pos2.y);
                }

                drawingContext.setLineDash([]);
            }

            // Draw particles
            fill('#FF0000');
            noStroke();
            currentPositions.forEach(pos => {
                circle(pos.x, pos.y, 8);
            });
        }
    </script>
</body>
</html>
