                    <input type="range" id="slider-n" min="2" max="20" step="1" value="6">
                </div>
                <div class="slider-container">
                    <label>T<sub>min</sub> (°C): <span id="value-tmin">25</span></label>
                    <input type="range" id="slider-tmin" min="0" max="50" step="1" value="25">
                </div>
                <div class="slider-container">
                    <label>T<sub>max</sub> (°C): <span id="value-tmax">75</span></label>
                    <input type="range" id="slider-tmax" min="51" max="100" step="1" value="75">
                </div>
            </div>

            <div class="control-section">
                <h3>Model Parameters</h3>
                <div class="slider-container">
                    <label>T<sub>ref</sub> (°C): <span id="value-tref">35</span></label>
                    <input type="range" id="slider-tref" min="0" max="100" step="1" value="35">
                </div>
                <div class="slider-container">
                    <label>c (°C<sup>-1</sup>): <span id="value-c">0.025</span></label>
