    <script>
        // Linear regression helper function
        function linearRegression(points) {
            const n = points.length;
            let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0, sumY2 = 0;

            points.forEach(point => {
                sumX += point[0];
                sumY += point[1];
                sumXY += point[0] * point[1];
                sumX2 += point[0] * point[0];
                sumY2 += point[1] * point[1];
            });

            const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
            const intercept = (sumY - slope * sumX) / n;

            const r2 = Math.pow((n * sumXY - sumX * sumY) / 
                Math.sqrt((n * sumX2 - sumX * sumX) * (n * sumY2 - sumY * sumY)), 2);

            return { slope, intercept, r2 };
        }

        // Main application logic
        let currentModel = "arrhenius";

        function updatePlots() {
            // Get current values
            const n = parseInt(document.getElementById("slider-n").value);
            const tMin = parseFloat(document.getElementById("slider-tmin").value);
            const tMax = parseFloat(document.getElementById("slider-tmax").value);
            const tRef = parseFloat(document.getElementById("slider-tref").value);
            const c = parseFloat(document.getElementById("slider-c").value);
            const b = parseFloat(document.getElementById("slider-b").value);
            const a = parseFloat(document.getElementById("slider-a").value);

            // Generate temperature points
            const temperatures = Array.from({length: n}, (_, i) => 
                tMin + (tMax - tMin) * i / (n - 1));

            // Generate data points
            let yValues;
            if (currentModel === "arrhenius") {
                yValues = temperatures.map(T => 
                    Math.exp(-a / (T + b)) / Math.exp(-a / (tRef + b)));
            } else {
                yValues = temperatures.map(T => 
                    Math.exp(c * (T - tRef)));
            }

            // Prepare fit data
            let fitPoints;
            if (currentModel === "arrhenius") {
                fitPoints = temperatures.map((T, i) => [
                    T - tRef,
                    Math.log(yValues[i])
                ]);
            } else {
                fitPoints = temperatures.map((T, i) => [
                    1 / (T + b),
                    Math.log(yValues[i])
                ]);
            }

            // Perform regression
            const regression = linearRegression(fitPoints);

            // Create plots
            const mainTrace = {
                x: temperatures,
                y: yValues,
                mode: 'markers',
                name: 'Data',
                marker: { color: '#d62728' }
            };

            let fitTrace;
            let mainTitle, bottomTitle;
            if (currentModel === "arrhenius") {
                const fittedC = regression.slope;
                const fitY = temperatures.map(T => 
                    Math.exp(fittedC * (T - tRef)));

                fitTrace = {
                    x: temperatures,
                    y: fitY,
                    mode: 'lines',
                    name: 'Exponential Fit',
                    line: { color: '#d62728' }
                };

                mainTitle = `Exponential model fit to Arrhenius data<br>c = ${fittedC.toFixed(4)}, R² = ${regression.r2.toFixed(4)}`;
                bottomTitle = `Linear fit of the exponential model<br>R² = ${regression.r2.toFixed(4)}`;
            } else {
                const fittedA = -regression.slope;
                const fitY = temperatures.map(T => 
                    Math.exp(-fittedA / (T + b)) / Math.exp(-fittedA / (tRef + b)));

                fitTrace = {
                    x: temperatures,
                    y: fitY,
                    mode: 'lines',
                    name: 'Arrhenius Fit',
                    line: { color: '#d62728' }
                };

                mainTitle = `Arrhenius model fit to exponential data<br>a = ${fittedA.toFixed(1)}, R² = ${regression.r2.toFixed(4)}`;
                bottomTitle = `Linear fit of the Arrhenius model<br>R² = ${regression.r2.toFixed(4)}`;
            }

            Plotly.newPlot('plot-top', [mainTrace, fitTrace], {
                title: mainTitle,
                xaxis: { title: 'T (°C)' },
                yaxis: { title: 'k(T)/k(T_ref)' }
            });

            const bottomTrace = {
                x: fitPoints.map(p => p[0]),
                y: fitPoints.map(p => p[1]),
                mode: 'markers',
                name: 'Transformed Data',
                marker: { color: currentModel === "arrhenius" ? '#2ca02c' : '#1f77b4' }
            };

            const bottomFitTrace = {
                x: fitPoints.map(p => p[0]),
                y: fitPoints.map(p => p[0] * regression.slope + regression.intercept),
                mode: 'lines',
                name: 'Linear Fit',
                line: { color: currentModel === "arrhenius" ? '#2ca02c' : '#1f77b4' }
            };

            Plotly.newPlot('plot-bottom', [bottomTrace, bottomFitTrace], {
                title: bottomTitle,
                xaxis: { 
                    title: currentModel === "arrhenius" ? 'T - T_ref' : '1/(T + b)',
                    zeroline: true
                },
                yaxis: { 
                    title: 'log(k(T)/k(T_ref))',
                    zeroline: true
                }
            });
        }

        // Event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', function() {
                document.getElementById(`value-${this.id.split('-')[1]}`).textContent = this.value;
                updatePlots();
            });
        });

        document.querySelectorAll('button').forEach(button => {
            button.addEventListener('click', function() {
                document.querySelectorAll('button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                currentModel = this.id.split('-')[1];
                updatePlots();
            });
        });

        // Initial plot
        updatePlots();
    </script>
