            border: 1px solid #ccc;
            border-radius: 5px;
            padding: 10px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="slider-container">
                <label for="slider-lambda">permeability ratio, λ:</label>
                <input type="range" id="slider-lambda" min="-8" max="-4" step="0.01" value="-5">
                <span id="value-lambda">0.00001</span>
            </div>
            <div class="slider-container">
                <label for="slider-omega">storativity ratio, ω:</label>
                <input type="range" id="slider-omega" min="-3" max="-1" step="0.01" value="-2">
                <span id="value-omega">0.01</span>
            </div>
            <div class="button-group">
                <label>option:</label>
                <button id="btn-pressure-drop" class="active">pressure drop</button>
                <button id="btn-difference">difference</button>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Bessel function approximations
        function besselK0(x) {
            if (x <= 2) {
                const t = x * x / 4;
                return -Math.log(x/2) * besselI0(x) - 0.57721566 + 
                       0.42278420*t + 0.23069756*Math.pow(t,2) + 0.03488590*Math.pow(t,3);
            }
            return Math.exp(-x) / Math.sqrt(x) * 
                   (1.25331414 - 0.07832358/x + 0.02189568/Math.pow(x,2));
        }

        function besselK1(x) {
            if (x <= 2) {
                const t = x * x / 4;
                return x * Math.log(x/2) * besselI1(x) + 1/x + 
                       0.15443144*x - 0.67278579*Math.pow(x,3)/18;
            }
            return Math.exp(-x) / Math.sqrt(x) * 
                   (1.25331414 + 0.23498619/x - 0.03655620/Math.pow(x,2));
        }

        function besselI0(x) {
            let sum = 1;
            let term = 1;
            for (let k = 1; k <= 10; k++) {
                term *= (x*x)/(4*k*k);
                sum += term;
            }
            return sum;
        }

        function besselI1(x) {
            let sum = x/2;
            let term = x/2;
            for (let k = 1; k <= 10; k++) {
                term *= (x*x)/(4*k*(k+1));
                sum += term;
            }
            return sum;
        }

        // Stehfest algorithm implementation
        function stehfest(func, t, N = 10) {
            const ln2 = Math.log(2);
            const h = [];

            for (let i = 1; i <= N; i++) {
                let sum = 0;
                const m = Math.min(i, N/2);
                for (let k = Math.floor((i+1)/2); k <= m; k++) {
                    sum += Math.pow(k, N/2) * factorial(2*k) /
                           (factorial(N/2-k) * factorial(k) * factorial(k-1) * factorial(i-k) * factorial(2*k-i));
                }
                h[i-1] = Math.pow(-1, i+N/2) * sum;
            }

            let sum = 0;
            for (let i = 0; i < N; i++) {
                sum += h[i] * func(ln2*(i+1)/t);
            }
            return ln2/t * sum;
        }

        function factorial(n) {
            if (n === 0) return 1;
            let result = 1;
            for (let i = 1; i <= n; i++) result *= i;
            return result;
        }

        // Main calculation function
        function calculatePressureDrop(tau, lambda, omega) {
            const laplaceSolution = (s) => {
                const f = (omega * (1-omega) * s + lambda) / ((1-omega) * s + lambda);
                const arg = Math.sqrt(s * f);
                return 1/s * besselK0(arg) / (arg * besselK1(arg));
            };

            return stehfest(laplaceSolution, tau);
        }

        // Plot updating function
        function updatePlot() {
            const lambda = Math.pow(10, parseFloat(document.getElementById('slider-lambda').value));
            const omega = Math.pow(10, parseFloat(document.getElementById('slider-omega').value));
            const isPressureDrop = document.getElementById('btn-pressure-drop').classList.contains('active');

            // Generate time points
            const tau = Array.from({length: 200}, (_, i) => 
                Math.pow(10, i/20)); // logarithmic spacing from 10^0 to 10^10

            // Calculate y values
            const yValues = tau.map(t => {
                const pressureDrop = calculatePressureDrop(t, lambda, omega);
                if (isPressureDrop) {
                    return pressureDrop;
                } else {
                    return pressureDrop - 0.5 * (Math.log(t) + 0.8091);
                }
            });

            const layout = {
                title: isPressureDrop ? 'pressure drawdown at the wellbore' : 'deviation from the asymptote',
                xaxis: {
                    title: 'dimensionless time, τ',
                    type: 'log',
                    range: [0, 10],
                    dtick: 2,
                    gridcolor: '#ddd'
                },
                yaxis: {
                    title: isPressureDrop ? 'dimensionless pressure drop, ΔP' : 
                                          'dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)',
                    range: isPressureDrop ? [0, 12] : [0, 3.5],
                    gridcolor: '#ddd'
                },
                margin: {l: 80, r: 50, b: 60, t: 60},
                paper_bgcolor: '#fff',
                plot_bgcolor: '#fff'
            };

            Plotly.newPlot('plot-container', [{
                x: tau,
                y: yValues,
                type: 'scatter',
                mode: 'lines',
                line: {color: 'red', width: 2}
            }], layout);
        }

        // Event listeners
        document.getElementById('slider-lambda').addEventListener('input', (e) => {
            const value = Math.pow(10, parseFloat(e.target.value));
            document.getElementById('value-lambda').textContent = value.toExponential(5);
            updatePlot();
        });

        document.getElementById('slider-omega').addEventListener('input', (e) => {
            const value = Math.pow(10, parseFloat(e.target.value));
            document.getElementById('value-omega').textContent = value.toExponential(2);
            updatePlot();
        });

        document.getElementById('btn-pressure-drop').addEventListener('click', (e) => {
            if (!e.target.classList.contains('active')) {
                document.getElementById('btn-difference').classList.remove('active');
                e.target.classList.add('active');
                updatePlot();
            }
        });

        document.getElementById('btn-difference').addEventListener('click', (e) => {
            if (!e.target.classList.contains('active')) {
                document.getElementById('btn-pressure-drop').classList.remove('active');
                e.target.classList.add('active');
                updatePlot();
            }
        });

        // Initial plot
        updatePlot();
    </script>
</body>
