        }
        #data-table th {
            background-color: #f2f2f2;
        }
        #results-container {
            margin-top: 20px;
        }
    </style>
</head>
<body>
    <div id="left-column">
        <div id="canvas-container"></div>
        <div id="results-container">
            <p>rejuvenation bias <i>u</i> = <span id="results-u">22.7319</span></p>
            <p>site productivity <i>p</i> = <span id="results-p">58.2729</span></p>
        </div>
    </div>
    <div id="right-column">
        <table id="data-table">
            <thead>
                <tr>
                    <th>age</th>
                    <th>volume</th>
                </tr>
            </thead>
            <tbody id="data-table-body"></tbody>
        </table>
    </div>

    <script>
        let dataPoints = [
            { age: 30, volume: 289 },
            { age: 35, volume: 445 },
            { age: 40, volume: 565 },
            { age: 45, volume: 704 },
            { age: 50, volume: 833 },
            { age: 55, volume: 924 }
        ];

        let selectedPoint = null;
        let p = 58.2729;
        let u = 22.7319;

        function setup() {
            const canvas = createCanvas(500, 500);
            canvas.parent('canvas-container');
            updateTable();
            calculateFit();
        }

        function draw() {
            background('#FFFFE0');
            drawGrid();
            drawAxes();
            drawLabels();
            drawCurve();
            drawPoints();
        }

        function drawGrid() {
            stroke(200);
            strokeWeight(0.5);
            for(let x = 20; x <= 80; x += 10) {
                let px = map(x, 20, 80, 50, width-50);
                line(px, 50, px, height-50);
            }
            for(let y = 0; y <= 1400; y += 200) {
                let py = map(y, 0, 1400, height-50, 50);
                line(50, py, width-50, py);
            }
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(1);
            line(50, height-50, width-50, height-50); // x-axis
            line(50, 50, 50, height-50); // y-axis

            // Ticks and labels
            textSize(10);
            textAlign(CENTER);
            for(let x = 20; x <= 80; x += 10) {
                let px = map(x, 20, 80, 50, width-50);
                line(px, height-50, px, height-45);
                text(x, px, height-35);
            }
            textAlign(RIGHT);
            for(let y = 0; y <= 1400; y += 200) {
                let py = map(y, 0, 1400, height-50, 50);
                line(45, py, 50, py);
                text(y, 40, py+4);
            }
        }

        function drawLabels() {
            textSize(16);
            textAlign(CENTER);
            text("Forest Growth Curve", width/2, 30);
            text("Stand age, yr", width/2, height-10);

            push();
            translate(20, height/2);
            rotate(-PI/2);
            text("Wood volume, m³ ha⁻¹", 0, 0);
            pop();

            push();
            translate(width-20, height/2);
            rotate(-PI/2);
            text("4/5 law", 0, 0);
            pop();
        }

        function drawPoints() {
            for(let point of dataPoints) {
                let x = map(point.age, 20, 80, 50, width-50);
                let y = map(point.volume, 0, 1400, height-50, 50);
                fill('#0000CD');
                stroke(0);
                strokeWeight(1);
                ellipse(x, y, 8, 8);
            }
        }

        function drawCurve() {
            beginShape();
            noFill();
            stroke('#0000CD');
            strokeWeight(2);
            for(let age = u; age <= 80; age += 0.1) {
                let volume = p * Math.pow(age - u, 4/5);
                let x = map(age, 20, 80, 50, width-50);
                let y = map(volume, 0, 1400, height-50, 50);
                vertex(x, y);
            }
            endShape();
        }

        function mousePressed() {
            for(let point of dataPoints) {
                let x = map(point.age, 20, 80, 50, width-50);
                let y = map(point.volume, 0, 1400, height-50, 50);
                if(dist(mouseX, mouseY, x, y) < 5) {
                    selectedPoint = point;
                    return;
                }
            }

            // Add new point if not clicking existing point
            let age = map(mouseX, 50, width-50, 20, 80);
            let volume = map(mouseY, height-50, 50, 0, 1400);
            if(mouseX > 50 && mouseX < width-50 && mouseY > 50 && mouseY < height-50) {
                dataPoints.push({age: age, volume: volume});
                calculateFit();
                updateTable();
            }
        }

        function mouseDragged() {
            if(selectedPoint) {
                selectedPoint.age = constrain(map(mouseX, 50, width-50, 20, 80), 20, 80);
                selectedPoint.volume = constrain(map(mouseY, height-50, 50, 0, 1400), 0, 1400);
                calculateFit();
                updateTable();
            }
        }

        function mouseReleased() {
            selectedPoint = null;
        }

        function mouseDoubleClicked() {
            for(let i = 0; i < dataPoints.length; i++) {
                let x = map(dataPoints[i].age, 20, 80, 50, width-50);
                let y = map(dataPoints[i].volume, 0, 1400, height-50, 50);
                if(dist(mouseX, mouseY, x, y) < 5) {
                    dataPoints.splice(i, 1);
                    calculateFit();
                    updateTable();
                    return;
                }
            }
        }

        function calculateFit() {
            // Simple gradient descent implementation
            const learningRate = 0.0001;
            const iterations = 1000;

            for(let i = 0; i < iterations; i++) {
                let dp = 0, du = 0;
                for(let point of dataPoints) {
                    let diff = point.volume - p * Math.pow(point.age - u, 4/5);
                    dp += -2 * diff * Math.pow(point.age - u, 4/5);
                    du += -2 * diff * p * (-4/5) * Math.pow(point.age - u, -1/5);
                }
                p -= learningRate * dp;
                u = constrain(u - learningRate * du, 0, Math.min(...dataPoints.map(p => p.age)) - 1);
            }

            document.getElementById('results-p').textContent = p.toFixed(4);
            document.getElementById('results-u').textContent = u.toFixed(4);
        }

        function updateTable() {
            const tbody = document.getElementById('data-table-body');
            tbody.innerHTML = '';
            for(let point of dataPoints) {
                const row = document.createElement('tr');
                row.innerHTML = `
                    <td>${point.age.toFixed(1)}</td>
                    <td>${point.volume.toFixed(1)}</td>
                `;
                tbody.appendChild(row);
            }
        }
    </script>
</body>
</html>
