<!DOCTYPE html>
<html>
<head>
    <title>Visibility Polygon Demo</title>
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <style>
        body {
            margin: 0;
            padding: 20px;
            font-family: Arial, sans-serif;
        }
        #demo-container {
            width: 600px;
            margin: 0 auto;
        }
        #controls {
            display: flex;
            align-items: center;
            padding: 10px 0;
            gap: 10px;
        }
        #angle-slider {
            flex: 1;
        }
        #reset-button {
            width: 30px;
            height: 30px;
            border-radius: 50%;
            border: 1px solid #ccc;
            background: #f0f0f0;
            cursor: pointer;
            font-size: 20px;
            line-height: 1;
            padding: 0;
        }
        #canvas-container {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <div id="controls">
            <label>vertices sorted by angle:</label>
            <input type="range" id="angle-slider" min="0" max="38" step="1" value="3">
            <span id="angle-value">3</span>
            <button id="reset-button">+</button>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let sourcePoint = { x: 275, y: 275 };
        const outerPolygon = [[50,50], [550,50], [550,550], [50,550]];
        const holes = [
            [[100,100], [250,100], [250,150], [200,150], [200,200], [100,200]],
            [[300,100], [450,100], [450,250], [400,250], [400,200], [300,200]],
            [[100,350], [250,350], [250,500], [200,500], [200,450], [100,450]],
            [[300,400], [450,400], [450,500], [300,500]],
            [[300,300], [350,300], [350,350], [300,350]]
        ];

        let isDragging = false;
        let sortedVertices = [];
        let allEdges = [];
        let currentStep = 3;

        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');

            // Initialize UI controls
            const slider = document.getElementById('angle-slider');
            const resetBtn = document.getElementById('reset-button');

            slider.addEventListener('input', (e) => {
                currentStep = parseInt(e.target.value);
                document.getElementById('angle-value').textContent = currentStep;
            });

            resetBtn.addEventListener('click', () => {
                sourcePoint = { x: 275, y: 275 };
                currentStep = 3;
                slider.value = 3;
                document.getElementById('angle-value').textContent = 3;
            });

            // Initialize geometry
            collectGeometry();
        }

        function draw() {
            background(255);

            // Draw polygons
            stroke(170);
            strokeWeight(1);
            fill(255);

            beginShape();
            for(let pt of outerPolygon) {
                vertex(pt[0], pt[1]);
            }
            endShape(CLOSE);

            for(let hole of holes) {
                beginShape();
                for(let pt of hole) {
                    vertex(pt[0], pt[1]);
                }
                endShape(CLOSE);
            }

            // Draw vertices
            noStroke();
            fill(255,0,0);
            for(let pt of sortedVertices) {
                ellipse(pt.x, pt.y, 4, 4);
            }

            // Draw visibility polygon
            updateVisibility();

            // Draw source point
            fill(0,0,255);
            stroke(0,0,255);
            strokeWeight(1);
            ellipse(sourcePoint.x, sourcePoint.y, 8, 8);
        }

        function mousePressed() {
            const d = dist(mouseX, mouseY, sourcePoint.x, sourcePoint.y);
            if(d < 8) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if(isDragging) {
                sourcePoint.x = constrain(mouseX, 50, 550);
                sourcePoint.y = constrain(mouseY, 50, 550);
                sortVertices();
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function collectGeometry() {
            // Collect all vertices
            let vertices = [];

            for(let pt of outerPolygon) {
                vertices.push({x: pt[0], y: pt[1]});
            }

            for(let hole of holes) {
                for(let pt of hole) {
                    vertices.push({x: pt[0], y: pt[1]});
                }
            }

            sortedVertices = vertices;
            sortVertices();

            // Collect edges
            allEdges = [];
            for(let i = 0; i < outerPolygon.length; i++) {
                const next = (i + 1) % outerPolygon.length;
                allEdges.push({
                    x1: outerPolygon[i][0],
                    y1: outerPolygon[i][1],
                    x2: outerPolygon[next][0],
                    y2: outerPolygon[next][1]
                });
            }

            for(let hole of holes) {
                for(let i = 0; i < hole.length; i++) {
                    const next = (i + 1) % hole.length;
                    allEdges.push({
                        x1: hole[i][0],
                        y1: hole[i][1],
                        x2: hole[next][0],
                        y2: hole[next][1]
                    });
                }
            }
        }

        function sortVertices() {
            sortedVertices.sort((a, b) => {
                const angleA = atan2(a.y - sourcePoint.y, a.x - sourcePoint.x);
                const angleB = atan2(b.y - sourcePoint.y, b.x - sourcePoint.x);
                return angleA - angleB;
            });
        }

        function updateVisibility() {
            if(currentStep > 0) {
                // Draw partial visibility polygon
                fill(255,255,153);
                stroke(170,170,170,127);
                beginShape();
                vertex(sourcePoint.x, sourcePoint.y);
                for(let i = 0; i < min(currentStep, sortedVertices.length); i++) {
                    const pt = findIntersection(sortedVertices[i]);
                    if(pt) {
                        vertex(pt.x, pt.y);
                    }
                }
                endShape(CLOSE);

                if(currentStep < sortedVertices.length) {
                    // Draw helper graphics
                    stroke(0,0,255);
                    line(sourcePoint.x, sourcePoint.y, 
                         sortedVertices[currentStep].x, 
                         sortedVertices[currentStep].y);

                    // Draw sweep arc
                    noFill();
                    const startAngle = atan2(sortedVertices[0].y - sourcePoint.y,
                                          sortedVertices[0].x - sourcePoint.x);
                    const endAngle = atan2(sortedVertices[currentStep].y - sourcePoint.y,
                                        sortedVertices[currentStep].x - sourcePoint.x);
                    arc(sourcePoint.x, sourcePoint.y, 30, 30, startAngle, endAngle);
                }
            }
        }

        function findIntersection(vertex) {
            const ray = {
                x1: sourcePoint.x,
                y1: sourcePoint.y,
                x2: vertex.x,
                y2: vertex.y
            };

            let closest = null;
            let minDist = Infinity;

            for(let edge of allEdges) {
                const intersection = lineIntersection(ray, edge);
                if(intersection) {
                    const d = dist(sourcePoint.x, sourcePoint.y,
                                 intersection.x, intersection.y);
                    if(d < minDist) {
                        minDist = d;
                        closest = intersection;
                    }
                }
            }

            return closest;
        }

        function lineIntersection(line1, line2) {
            const x1 = line1.x1, y1 = line1.y1;
            const x2 = line1.x2, y2 = line1.y2;
            const x3 = line2.x1, y3 = line2.y1;
            const x4 = line2.x2, y4 = line2.y2;

            const den = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4);
            if(den === 0) return null;

            const t = ((x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)) / den;
            const u = -((x1 - x2) * (y1 - y3) - (y1 - y2) * (x1 - x3)) / den;

            if(t >= 0 && t <= 1 && u >= 0 && u <= 1) {
                return {
                    x: x1 + t * (x2 - x1),
                    y: y1 + t * (y2 - y1)
                };
            }
            return null;
        }
    </script>
</body>
</html>
