<!DOCTYPE html>
<html>
<head>
    <title>ODE Power Series Visualization</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        #main-container {
            display: flex;
            height: 100vh;
            margin: 0;
            padding: 20px;
            box-sizing: border-box;
        }

        #control-panel {
            width: 250px;
            padding: 20px;
            background: #f5f5f5;
        }

        #control-panel div {
            margin-bottom: 20px;
        }

        #control-panel label {
            display: block;
            margin-bottom: 5px;
            font-weight: bold;
        }

        #control-panel input {
            width: 100%;
        }

        #visualization-area {
            flex-grow: 1;
            padding: 20px;
        }

        #plot-container {
            width: 100%;
            height: 100%;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-order">expansion order</label>
                <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                <span id="value-order">5</span>
            </div>
            <div>
                <label for="slider-a">f(0)</label>
                <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                <span id="value-a">2.0</span>
            </div>
            <div>
                <label for="slider-b">f'(0)</label>
                <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                <span id="value-b">0.0</span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        function factorial(n) {
            return n <= 1 ? 1 : n * factorial(n - 1);
        }

        function calculateDerivatives(a, b) {
            const f_derivs = new Array(6);
            f_derivs[0] = a;
            f_derivs[1] = b;

            // f''(0)
            f_derivs[2] = -(f_derivs[1] + 1.5 * f_derivs[0]) / f_derivs[0];

            // f'''(0)
            f_derivs[3] = -(f_derivs[2]*f_derivs[1] + f_derivs[2] + 1.5*f_derivs[1]) / f_derivs[0];

            // f''''(0)
            f_derivs[4] = -(2*f_derivs[3]*f_derivs[1] + f_derivs[2]*f_derivs[2] + 
                           f_derivs[3] + 1.5*f_derivs[2]) / f_derivs[0];

            // f'''''(0)
            f_derivs[5] = -(3*f_derivs[4]*f_derivs[1] + 4*f_derivs[3]*f_derivs[2] + 
                           f_derivs[4] + 1.5*f_derivs[3]) / f_derivs[0];

            return f_derivs.map((d, i) => d / factorial(i));
        }

        function rk4Step(x, y, h) {
            function deriv(x, y) {
                return [
                    y[1],
                    -(y[1] + 1.5*y[0]) / y[0]
                ];
            }

            const k1 = deriv(x, y);
            const k2 = deriv(x + h/2, [
                y[0] + h/2 * k1[0],
                y[1] + h/2 * k1[1]
            ]);
            const k3 = deriv(x + h/2, [
                y[0] + h/2 * k2[0],
                y[1] + h/2 * k2[1]
            ]);
            const k4 = deriv(x + h, [
                y[0] + h * k3[0],
                y[1] + h * k3[1]
            ]);

            return [
                y[0] + h/6 * (k1[0] + 2*k2[0] + 2*k3[0] + k4[0]),
                y[1] + h/6 * (k1[1] + 2*k2[1] + 2*k3[1] + k4[1])
            ];
        }

        function formatPolynomial(coeffs, order) {
            let terms = [];
            for(let i = 0; i <= order; i++) {
                if(Math.abs(coeffs[i]) < 1e-10) continue;

                let coeff = coeffs[i].toFixed(5).replace(/\.?0+$/, '');
                if(i === 0) {
                    terms.push(coeff);
                } else if(i === 1) {
                    terms.push(`${coeff}x`);
                } else {
                    terms.push(`${coeff}x^${i}`);
                }
            }

            return `f(x) = ${terms.join(' + ').replace(/\+ -/g, '- ')}`;
        }

        function updatePlot() {
            const order = parseInt(document.getElementById('slider-order').value);
            const a = parseFloat(document.getElementById('slider-a').value);
            const b = parseFloat(document.getElementById('slider-b').value);

            document.getElementById('value-order').textContent = order;
            document.getElementById('value-a').textContent = a.toFixed(1);
            document.getElementById('value-b').textContent = b.toFixed(1);

            const coeffs = calculateDerivatives(a, b);

            // Generate x values
            const x = Array.from({length: 201}, (_, i) => i * 1.5 / 200);

            // Calculate power series
            const y_approx = x.map(xi => {
                let sum = 0;
                for(let k = 0; k <= order; k++) {
                    sum += coeffs[k] * Math.pow(xi, k);
                }
                return sum;
            });

            // Calculate numerical solution
            const y_numerical = [];
            let y = [a, b];
            y_numerical.push(a);
            for(let i = 1; i < x.length; i++) {
                const h = x[i] - x[i-1];
                y = rk4Step(x[i-1], y, h);
                y_numerical.push(y[0]);
            }

            // Find agreement region
            let x_limit = 1.5;
            for(let i = 0; i < x.length; i++) {
                if(Math.abs(y_approx[i] - y_numerical[i]) >= 0.01) {
                    x_limit = x[i];
                    break;
                }
            }

            const layout = {
                title: formatPolynomial(coeffs, order),
                xaxis: { title: 'x', range: [0, 1.5], zeroline: false },
                yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
                margin: { l: 50, r: 20, t: 50, b: 40 },
                shapes: [{
                    type: 'rect',
                    xref: 'x',
                    yref: 'paper',
                    x0: 0,
                    y0: 0,
                    x1: x_limit,
                    y1: 1,
                    fillcolor: 'lightblue',
                    opacity: 0.4,
                    layer: 'below',
                    line: { width: 0 }
                }]
            };

            const traceRed = {
                x: x,
                y: y_approx,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'red', width: 2 },
                name: 'Power Series'
            };

            const traceGreen = {
                x: x,
                y: y_numerical,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'green', width: 3 },
                name: 'Numerical'
            };

            Plotly.react('plot-container', [traceRed, traceGreen], layout);
        }

        // Add event listeners
        document.getElementById('slider-order').addEventListener('input', updatePlot);
        document.getElementById('slider-a').addEventListener('input', updatePlot);
        document.getElementById('slider-b').addEventListener('input', updatePlot);

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
