            border-radius: 4px;
            border: 1px solid #ccc;
        }

        label {
            font-weight: bold;
            margin-right: 10px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls">
            <div id="text-control">
                <label for="select-text">Text:</label>
                <select id="select-text">
                    <option>To Be Or Not To Be</option>
                    <option>The quick brown fox jumps over the lazy dog</option>
                    <option>It was the best of times, it was the worst of times</option>
                </select>
            </div>
            <div id="view-controls">
                <label>Plot:</label>
                <button id="btn-plot-2d" class="active">distribution 2D</button>
                <button id="btn-plot-3d">keyboard 3D</button>
                <label>Keyboard:</label>
                <button id="btn-keyboard-qwerty" class="active">QWERTY</button>
                <button id="btn-keyboard-dvorak">Dvorak</button>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="visualization-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        // Global variables
        let scene, camera, renderer, currentView = '2d';
        let currentText = 'To Be Or Not To Be';
        let currentLayout = 'qwerty';

        // Initialize Three.js scene
        function init() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            renderer = new THREE.WebGLRenderer({
                canvas: document.getElementById('visualization-canvas'),
                antialias: true
            });
            renderer.setSize(900, 600);

            // Add lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);
            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(10, 10, 10);
            scene.add(directionalLight);

            // Set initial camera
            setupCamera2D();

            // Initial render
            updateVisualization();
        }

        function setupCamera2D() {
            camera = new THREE.OrthographicCamera(-15, 15, 10, -10, 1, 1000);
            camera.position.set(0, 0, 50);
            camera.lookAt(0, 0, 0);
        }

        function setupCamera3D() {
            camera = new THREE.PerspectiveCamera(45, 900/600, 0.1, 1000);
            camera.position.set(20, 30, 50);
            camera.lookAt(0, 0, 0);
        }

        function calculateFrequencies(text) {
            const frequencies = {};
            'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('').forEach(c => frequencies[c] = 0);

            text.toUpperCase().split('').forEach(char => {
                if(/[A-Z]/.test(char)) {
                    frequencies[char]++;
                }
            });
            return frequencies;
        }

        function updateVisualization() {
            // Clear existing scene
            while(scene.children.length > 0) { 
                scene.remove(scene.children[0]); 
            }

            const frequencies = calculateFrequencies(currentText);

            if(currentView === '2d') {
                render2DView(frequencies);
            } else {
                render3DView(frequencies);
            }

            renderer.render(scene, camera);
        }

        // Event listeners
        document.getElementById('select-text').addEventListener('change', (e) => {
            currentText = e.target.value;
            updateVisualization();
        });

        document.getElementById('btn-plot-2d').addEventListener('click', () => {
            currentView = '2d';
            setupCamera2D();
            document.getElementById('btn-plot-2d').classList.add('active');
            document.getElementById('btn-plot-3d').classList.remove('active');
            updateVisualization();
        });

        document.getElementById('btn-plot-3d').addEventListener('click', () => {
            currentView = '3d';
            setupCamera3D();
            document.getElementById('btn-plot-3d').classList.add('active');
            document.getElementById('btn-plot-2d').classList.remove('active');
            updateVisualization();
        });

        document.getElementById('btn-keyboard-qwerty').addEventListener('click', () => {
            currentLayout = 'qwerty';
            document.getElementById('btn-keyboard-qwerty').classList.add('active');
            document.getElementById('btn-keyboard-dvorak').classList.remove('active');
            if(currentView === '3d') updateVisualization();
        });

        document.getElementById('btn-keyboard-dvorak').addEventListener('click', () => {
            currentLayout = 'dvorak';
            document.getElementById('btn-keyboard-dvorak').classList.add('active');
            document.getElementById('btn-keyboard-qwerty').classList.remove('active');
            if(currentView === '3d') updateVisualization();
        });

        function render2DView(frequencies) {
            // Implement 2D bar chart visualization
            let maxFreq = Math.max(...Object.values(frequencies));
            let index = 0;

            Object.entries(frequencies).forEach(([letter, freq]) => {
                const height = (freq/maxFreq) * 10;
                const geometry = new THREE.BoxGeometry(0.8, height, 0.8);
                const material = new THREE.MeshStandardMaterial({
                    color: new THREE.Color().setHSL(freq/maxFreq * 0.3, 0.8, 0.5)
                });
                const bar = new THREE.Mesh(geometry, material);
                bar.position.set(index * 1 - 12, height/2, 0);
                scene.add(bar);
                index++;
            });
        }

        function render3DView(frequencies) {
            // Implement 3D keyboard visualization
            const layouts = {
                qwerty: [
                    ['Q','W','E','R','T','Y','U','I','O','P'],
                    ['A','S','D','F','G','H','J','K','L'],
                    ['Z','X','C','V','B','N','M']
                ],
                dvorak: [
                    ['P','Y','F','G','C','R','L'],
                    ['A','O','E','U','I','D','H','T','N','S'],
                    ['Q','J','K','X','B','M','W','V','Z']
                ]
            };

            const currentKeys = layouts[currentLayout];
            let maxFreq = Math.max(...Object.values(frequencies));

            currentKeys.forEach((row, rowIndex) => {
                row.forEach((letter, colIndex) => {
                    const height = (frequencies[letter]/maxFreq) * 5 + 0.5;
                    const geometry = new THREE.CylinderGeometry(0.8, 0.8, height, 32);
                    const material = new THREE.MeshStandardMaterial({
                        color: new THREE.Color().setHSL(rowIndex * 0.3, 0.8, 0.5)
                    });
                    const key = new THREE.Mesh(geometry, material);
                    key.position.set(colIndex * 2 - row.length, height/2, rowIndex * 2);
                    scene.add(key);
                });
            });
        }

        // Initialize on page load
        init();
    </script>
</body>
</html>
