    </style>
</head>
<body>
    <div id="container">
        <div id="header">
            <h1>Plane Seismic Waves</h1>
            <p>This simulation demonstrates the propagation of pressure (P) and shear (S) seismic waves through a medium.</p>
        </div>

        <div id="control-panel">
            <div class="control-row">
                <label for="slider-time">time</label>
                <input type="range" id="slider-time" min="0" max="5" step="0.01" value="0">
                <span id="label-time" class="value-label">0</span>
                <button id="btn-play-pause">Play</button>
                <button id="btn-reset">Reset</button>
            </div>

            <div class="control-row">
                <label for="slider-trail">trail length</label>
                <input type="range" id="slider-trail" min="0" max="100" step="1" value="5">
                <span id="label-trail" class="value-label">5</span>
            </div>

            <div class="control-row">
                <label for="slider-points">number of points</label>
                <input type="range" id="slider-points" min="10" max="150" step="1" value="50">
                <span id="label-points" class="value-label">50</span>
            </div>

            <hr>

            <h3>pressure wave</h3>
            <div class="wave-icons">
                <svg width="40" height="20"><path d="M0 10 C 5 0, 15 20, 20 10 S 25 0, 30 10 S 35 20, 40 10" stroke="black" fill="none"/></svg>
                <svg width="40" height="20"><path d="M0 10 H 10 C 15 10, 15 0, 20 0 S 25 10, 30 10 H 40" stroke="black" fill="none"/></svg>
            </div>

            <div class="control-row">
                <label for="slider-p-amp">amplitude</label>
                <input type="range" id="slider-p-amp" min="0" max="0.4" step="0.001" value="0.2">
                <span id="label-p-amp" class="value-label">0.2</span>
            </div>

            <div class="control-row">
                <label for="slider-p-wl">wavelength</label>
                <input type="range" id="slider-p-wl" min="0.5" max="5" step="0.01" value="3">
                <span id="label-p-wl" class="value-label">3</span>
            </div>

            <h3>shear wave</h3>
            <div class="wave-icons">
                <svg width="40" height="20"><path d="M0 10 C 5 0, 15 20, 20 10 S 25 0, 30 10 S 35 20, 40 10" stroke="black" fill="none"/></svg>
                <svg width="40" height="20"><path d="M0 10 H 10 C 15 10, 15 0, 20 0 S 25 10, 30 10 H 40" stroke="black" fill="none"/></svg>
            </div>

            <div class="control-row">
                <label for="slider-s-amp">amplitude</label>
                <input type="range" id="slider-s-amp" min="0" max="0.4" step="0.001" value="0.1">
                <span id="label-s-amp" class="value-label">0.1</span>
            </div>

            <div class="control-row">
                <label for="slider-s-wl">wavelength</label>
                <input type="range" id="slider-s-wl" min="0.5" max="5" step="0.01" value="1">
                <span id="label-s-wl" class="value-label">1</span>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        let particles = [];
        let isPlaying = false;

        class Particle {
            constructor(x0) {
                this.x0 = x0;
                this.history = [];
            }
        }

        function setup() {
            const canvas = createCanvas(800, 400);
            canvas.parent('canvas-container');
            initializeParticles();
            setupEventListeners();
        }

        function initializeParticles() {
            particles = [];
            const numPoints = parseInt(document.getElementById('slider-points').value);
            for(let i = 0; i < numPoints; i++) {
                particles.push(new Particle(map(i, 0, numPoints-1, 0, 5)));
            }
        }

        function setupEventListeners() {
            const sliders = document.querySelectorAll('input[type="range"]');
            sliders.forEach(slider => {
                slider.addEventListener('input', function() {
                    document.getElementById(`label-${slider.id.split('-')[1]}`).textContent = this.value;
                    if(slider.id === 'slider-points') initializeParticles();
                });
            });

            document.getElementById('btn-play-pause').addEventListener('click', function() {
                isPlaying = !isPlaying;
                this.textContent = isPlaying ? 'Pause' : 'Play';
            });

            document.getElementById('btn-reset').addEventListener('click', reset);
        }

        function reset() {
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = 'Play';
            document.querySelectorAll('input[type="range"]').forEach(slider => {
                slider.value = slider.defaultValue;
                document.getElementById(`label-${slider.id.split('-')[1]}`).textContent = slider.defaultValue;
            });
            initializeParticles();
        }

        function draw() {
            background(255);

            if(isPlaying) {
                const timeSlider = document.getElementById('slider-time');
                let time = parseFloat(timeSlider.value);
                time = (time + 0.01) % 5;
                timeSlider.value = time;
                document.getElementById('label-time').textContent = time.toFixed(2);
            }

            // Get current parameters
            const time = parseFloat(document.getElementById('slider-time').value);
            const p_amp = parseFloat(document.getElementById('slider-p-amp').value);
            const p_wl = parseFloat(document.getElementById('slider-p-wl').value);
            const s_amp = parseFloat(document.getElementById('slider-s-amp').value);
            const s_wl = parseFloat(document.getElementById('slider-s-wl').value);
            const trailLength = parseInt(document.getElementById('slider-trail').value);

            // Draw axes
            drawAxes();

            // Update and draw particles
            particles.forEach(particle => {
                const dx = p_amp * sin(TWO_PI * (particle.x0 / p_wl - time));
                const dy = s_amp * sin(TWO_PI * (particle.x0 / s_wl - time));

                const x_final = map(particle.x0 + dx, 0, 5, 50, width-50);
                const y_final = map(dy, -0.5, 0.5, height-50, 50);

                // Draw vertical guide line
                stroke(211,211,211);
                line(x_final, 50, x_final, height-50);

                // Update and draw trail
                particle.history.unshift({x: x_final, y: y_final});
                if(particle.history.length > trailLength) particle.history.pop();

                if(trailLength > 0) {
                    for(let i = 0; i < particle.history.length; i++) {
                        const alpha = map(i, 0, particle.history.length, 255, 50);
                        fill(106, 90, 205, alpha);
                        noStroke();
                        circle(particle.history[i].x, particle.history[i].y, 3);
                    }
                }

                // Draw current position
                fill(106, 90, 205);
                noStroke();
                circle(x_final, y_final, 6);
            });
        }

        function drawAxes() {
            stroke(0);
            // X axis
            line(50, height/2, width-50, height/2);
            // Y axis
            line(50, 50, 50, height-50);

            // Ticks
            for(let x = 0; x <= 5; x++) {
                const xPos = map(x, 0, 5, 50, width-50);
                line(xPos, height/2-5, xPos, height/2+5);
                textAlign(CENTER);
                text(x, xPos, height/2+20);
            }

            for(let y = -0.4; y <= 0.4; y += 0.2) {
                const yPos = map(y, -0.5, 0.5, height-50, 50);
                line(45, yPos, 55, yPos);
                textAlign(RIGHT);
                text(y.toFixed(1), 40, yPos+4);
            }
        }
    </script>
</body>
</html>
