            background: #4CAF50;
            color: white;
            border-color: #4CAF50;
        }

        #plot-container {
            height: 600px;
            border: 1px solid #ddd;
            border-radius: 8px;
        }

        .value-display {
            color: #666;
            font-size: 0.9em;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-dip">Fault dip (°)</label>
                <input type="range" id="slider-dip" min="0" max="90" step="1" value="0">
                <span id="value-dip" class="value-display">0</span>
            </div>

            <div class="control-group">
                <label for="slider-depth">Fault depth (m)</label>
                <input type="range" id="slider-depth" min="100" max="1000" step="10" value="500">
                <span id="value-depth" class="value-display">500</span>
            </div>

            <div class="control-group">
                <label for="slider-limit">X and Y plot limit (m)</label>
                <input type="range" id="slider-limit" min="500" max="2000" step="50" value="1000">
                <span id="value-limit" class="value-display">1000</span>
            </div>

            <div class="control-group">
                <label>Displacement component</label>
                <div id="btn-group-component" class="btn-group">
                    <button id="btn-component-x">X</button>
                    <button id="btn-component-y">Y</button>
                    <button id="btn-component-z" class="active">Z</button>
                </div>
            </div>

            <div class="control-group">
                <label>Fault type</label>
                <div id="btn-group-fault-type" class="btn-group">
                    <button id="btn-fault-tensile" class="active">tensile</button>
                    <button id="btn-fault-strike-slip">strike-slip</button>
                    <button id="btn-fault-normal">normal</button>
                </div>
            </div>
        </div>

        <div id="plot-container">
            <div id="plot"></div>
        </div>
    </div>

    <script>
        function calculateDisplacements(x, y, dip, depth, faultType, nu = 0.25) {
            // Simplified displacement calculation (placeholder)
            const r = Math.sqrt(x*x + y*y + depth*depth);
            const ux = (faultType === 'strike-slip') ? x/r : y/r;
            const uy = (faultType === 'normal') ? y/r : -x/r;
            const uz = (faultType === 'tensile') ? depth/r : -depth/r;
            return {ux, uy, uz};
        }

        function updatePlot() {
            const dip = parseFloat(document.getElementById('slider-dip').value);
            const depth = parseFloat(document.getElementById('slider-depth').value);
            const limit = parseFloat(document.getElementById('slider-limit').value);

            const component = document.querySelector('#btn-group-component button.active').textContent;
            const faultType = document.querySelector('#btn-group-fault-type button.active').id.replace('btn-fault-', '');

            const gridSize = 50;
            const x = Array.from({length: gridSize}, (_, i) => -limit + 2*limit*i/(gridSize-1));
            const y = Array.from({length: gridSize}, (_, i) => -limit + 2*limit*i/(gridSize-1));
            const z = Array.from({length: gridSize}, () => new Array(gridSize));

            for(let i = 0; i < gridSize; i++) {
                for(let j = 0; j < gridSize; j++) {
                    const disp = calculateDisplacements(x[i], y[j], dip*Math.PI/180, depth, faultType);
                    z[j][i] = (component === 'X' ? disp.ux : component === 'Y' ? disp.uy : disp.uz) * 1000;
                }
            }

            const data = [{
                type: 'surface',
                x: x,
                y: y,
                z: z,
                colorscale: [['0', '#FFA500'], ['1', '#FFD700']],
                showscale: false,
                contours: {
                    x: { show: true, color: 'black', width: 0.5 },
                    y: { show: true, color: 'black', width: 0.5 },
                    z: { show: false }
                },
                lighting: {
                    ambient: 0.8,
                    diffuse: 0.8,
                    specular: 0.2
                }
            }];

            const layout = {
                scene: {
                    xaxis: { title: 'x (m)', range: [-limit, limit] },
                    yaxis: { title: 'y (m)', range: [-limit, limit] },
                    zaxis: { title: 'displacement (mm)', autorange: true },
                    camera: { eye: { x: 1.8, y: 1.8, z: 0.8 } }
                },
                margin: { l: 20, r: 20, b: 20, t: 20 }
            };

            Plotly.react('plot', data, layout);
        }

        // Event listeners
        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', () => {
                document.getElementById(`value-${slider.id.split('-')[1]}`).textContent = slider.value;
                updatePlot();
            });
        });

        document.querySelectorAll('.btn-group button').forEach(button => {
            button.addEventListener('click', () => {
                button.parentElement.querySelector('.active').classList.remove('active');
                button.classList.add('active');
                updatePlot();
            });
        });

        // Initialize plot
        updatePlot();
    </script>
</body>
</html>
