    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        // Bessel function zeros (pre-calculated)
        const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];

        // Bessel J0 function approximation
        function besselJ0(x) {
            if (x === 0) return 1;

            let j0 = 1;
            let x2 = x * x;
            j0 += x2 * (-0.25);
            x2 *= x * x / (3 * 4);
            j0 += x2 * (0.015625);
            return j0;
        }

        let canvas;

        function setup() {
            canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
        }

        function draw() {
            background(240);

            const t = parseFloat(document.getElementById('slider-t').value);
            const k = parseInt(document.getElementById('slider-k').value);
            const showAxes = document.getElementById('checkbox-axes').checked;

            // Calculate parameters
            const z_k = zeros[k-1];
            const omega_k = z_k / 2;
            const timeFactor = cos(omega_k * t);
            const amplitude = 0.8;

            // Setup coordinate transformation
            translate(width/2, 50);
            scale(width/4, height/1.2);

            // Draw axes if enabled
            if (showAxes) {
                drawAxes();
            }

            // Draw string
            noFill();
            stroke(0, 0, 255);
            strokeWeight(2/width);

            beginShape();
            for (let y_norm = 0; y_norm <= 1; y_norm += 0.01) {
                const bessel_arg = z_k * sqrt(y_norm);
                const x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
                const y_pos = -y_norm;
                vertex(x_pos, y_pos);
            }
            endShape();
        }

        function drawAxes() {
            stroke(150);
            strokeWeight(1/width);

            // X axis
            line(-2, 0, 2, 0);
            for (let x = -2; x <= 2; x++) {
                if (x !== 0) {
                    line(x, -0.02, x, 0.02);
                    textSize(0.1);
                    text(x.toString(), x - 0.05, 0.1);
                }
            }

            // Y axis
            line(0, 0, 0, -1.2);
            for (let y = -1; y <= 0; y += 0.2) {
                if (y !== 0) {
                    line(-0.02, y, 0.02, y);
                    text(y.toFixed(1), 0.05, y);
                }
            }
        }

        // UI event handlers
        document.getElementById('slider-t').addEventListener('input', function(e) {
            document.getElementById('t-value-display').textContent = parseFloat(e.target.value).toFixed(2);
        });

        document.getElementById('slider-k').addEventListener('input', function(e) {
            const k = parseInt(e.target.value);
            document.getElementById('k-value-display').textContent = k;
            const omega_k = (zeros[k-1] / 2).toFixed(2);
            document.getElementById('formula-display').innerHTML = `\\(\\omega_{${k}} = ${omega_k} \\sqrt{g/l}\\)`;
            MathJax.typeset();
        });
    </script>
</body>
</html>
