<!DOCTYPE html>
<html>
<head>
    <title>Fugacity Coefficient Calculator</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        #app-container {
            max-width: 800px;
            margin: 0 auto;
            padding: 20px;
        }
        #control-panel {
            margin-bottom: 20px;
            padding: 15px;
            background: #f5f5f5;
            border-radius: 5px;
        }
        .button-group {
            margin-bottom: 10px;
        }
        button {
            padding: 5px 15px;
            margin-right: 10px;
            cursor: pointer;
        }
        button.selected {
            border: 2px solid red;
        }
        .slider-container {
            margin: 10px 0;
        }
        #slider-pressure {
            width: 300px;
            margin: 0 10px;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="button-group">
                <button id="btn-srk" class="selected">SRK</button>
                <button id="btn-pr">PR</button>
            </div>
            <div>
                <input type="checkbox" id="checkbox-aspen" checked>
                <label for="checkbox-aspen">comparison with Aspen Plus</label>
            </div>
            <div class="slider-container">
                <label for="slider-pressure">pressure P</label>
                <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
                <span id="display-pressure">10</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Constants
        const R = 0.0831446;
        const Tc = 365.57;
        const Pc = 46.65;
        const w = 0.144;

        // Cubic equation solver
        function solveCubic(a, b, c) {
            const p = b - a*a/3;
            const q = 2*a*a*a/27 - a*b/3 + c;
            const D = q*q/4 + p*p*p/27;

            if (D > 0) {
                const u = Math.cbrt(-q/2 + Math.sqrt(D));
                const v = Math.cbrt(-q/2 - Math.sqrt(D));
                return u + v - a/3;
            } else {
                const phi = Math.acos(-q/(2*Math.sqrt(-p*p*p/27)));
                const roots = [
                    2*Math.sqrt(-p/3)*Math.cos(phi/3),
                    2*Math.sqrt(-p/3)*Math.cos((phi + 2*Math.PI)/3),
                    2*Math.sqrt(-p/3)*Math.cos((phi + 4*Math.PI)/3)
                ].map(x => x - a/3);
                return Math.max(...roots);
            }
        }

        // Calculate fugacity coefficient
        function calculateFugacity(T, P, type) {
            const Tr = T/Tc;
            let a, b, m, alpha;

            if (type === 'srk') {
                a = 0.42748 * R*R * Tc*Tc / Pc;
                b = 0.08664 * R * Tc / Pc;
                m = 0.480 + 1.574*w - 0.176*w*w;
                alpha = Math.pow(1 + m*(1 - Math.sqrt(Tr)), 2);
            } else {
                a = 0.45724 * R*R * Tc*Tc / Pc;
                b = 0.07780 * R * Tc / Pc;
                m = 0.37464 + 1.54226*w - 0.26992*w*w;
                alpha = Math.pow(1 + m*(1 - Math.sqrt(Tr)), 2);
            }

            const a_eff = a * alpha;
            const A = a_eff * P / (R*T*R*T);
            const B = b * P / (R*T);

            let Z;
            if (type === 'srk') {
                Z = solveCubic(-1, A-B-B*B, -A*B);
            } else {
                Z = solveCubic(-(1-B), A-3*B*B-2*B, -(A*B-B*B-B*B*B));
            }

            let lnphi;
            if (type === 'srk') {
                lnphi = Z - 1 - Math.log(Z - B) - (A/B)*Math.log(1 + B/Z);
            } else {
                lnphi = Z - 1 - Math.log(Z - B) - (A/(2*Math.sqrt(2)*B))*Math.log((Z + (1+Math.sqrt(2))*B)/(Z + (1-Math.sqrt(2))*B));
            }

            return Math.exp(lnphi);
        }

        // Plot data
        function updatePlot() {
            const pressure = Number(document.getElementById('slider-pressure').value);
            const showAspen = document.getElementById('checkbox-aspen').checked && pressure === 10;
            const eosType = document.getElementById('btn-srk').classList.contains('selected') ? 'srk' : 'pr';

            // Generate temperature points and calculate fugacity
            const temp = Array.from({length: 201}, (_, i) => 300 + i);
            const phi = temp.map(T => calculateFugacity(T, pressure, eosType));

            const traces = [{
                x: temp,
                y: phi,
                type: 'scatter',
                mode: 'lines',
                line: {
                    color: eosType === 'srk' ? 'red' : 'blue',
                    width: 2
                },
                name: eosType.toUpperCase()
            }];

            if (showAspen) {
                traces.push({
                    x: [300,310,320,330,340,350,360,370,380,390,400],
                    y: [0.868,0.881,0.891,0.900,0.909,0.917,0.924,0.930,0.937,0.942,0.946],
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'green',
                        symbol: 'square',
                        size: 8
                    },
                    name: 'Aspen Data'
                });
            }

            const layout = {
                xaxis: {
                    title: 'temperature (K)',
                    range: [295, 505],
                    showgrid: true
                },
                yaxis: {
                    title: 'gas-phase fugacity coefficient',
                    range: [0.68, 1.02],
                    showgrid: true
                },
                plot_bgcolor: '#e5e5e5',
                paper_bgcolor: 'white'
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        // Event listeners
        document.getElementById('btn-srk').addEventListener('click', function() {
            this.classList.add('selected');
            document.getElementById('btn-pr').classList.remove('selected');
            updatePlot();
        });

        document.getElementById('btn-pr').addEventListener('click', function() {
            this.classList.add('selected');
            document.getElementById('btn-srk').classList.remove('selected');
            updatePlot();
        });

        document.getElementById('slider-pressure').addEventListener('input', function() {
            document.getElementById('display-pressure').textContent = this.value;
            updatePlot();
        });

        document.getElementById('checkbox-aspen').addEventListener('change', updatePlot);

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
